      // Get basic student info with related data
      const studentQuery = `
        SELECT 
          s.id, s.student_id as student_code, s.name, s.email, s.phone, s.parent_phone,
          s.date_of_birth, s.enrollment_date, s.pending_amount, s.paid_amount,
