  ];

  const courseTypes = [
    { value: 'monthly_tuition', label: 'Monthly Tuition' },
    { value: 'fixed_fee', label: 'Fixed Fee Course' },
  ];

  return (
    <div className="space-y-8">
      <Card>
        <CardHeader>
          <CardTitle>Payment Management</CardTitle>
