  );
}

client/src/components/progress/ProgressT.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import { apiRequest } from '@/lib/queryClient';
import { Save } from 'lucide-react';

export function ProgressTracker() {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedChapter, setSelectedChapter] = useState('');
  const [topicProgress, setTopicProgress] = useState<{[key: string]: boolean}>({});

  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Fetch students
  const { data: students = [] } = useQuery({
    queryKey: ['/api/students'],
    queryFn: async () => {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/students?soCenterId=${user?.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!user,
  });

  // Fetch subjects based on selected student's class
  const { data: subjects = [] } = useQuery({
    queryKey: ['/api/subjects', selectedStudent],
    queryFn: async () => {
      if (!selectedStudent) return [];
      const student = students.find((s: any) => s.id === selectedStudent);
      if (!student) return [];

      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/subjects/${student.classId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedStudent && students.length > 0,
  });

  // Fetch chapters based on selected subject
  const { data: chapters = [] } = useQuery({
    queryKey: ['/api/chapters', selectedSubject],
    queryFn: async () => {
      if (!selectedSubject) return [];
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/chapters/${selectedSubject}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedSubject,
  });

  // Fetch topics based on selected chapter
  const { data: topics = [] } = useQuery({
    queryKey: ['/api/topics', selectedChapter],
    queryFn: async () => {
      if (!selectedChapter) return [];
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/topics/${selectedChapter}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedChapter,
  });

  // Fetch existing progress for selected student
  const { data: existingProgress = [] } = useQuery({
    queryKey: ['/api/progress', selectedStudent],
    queryFn: async () => {
      if (!selectedStudent) return [];
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/progress/${selectedStudent}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedStudent,
  });

  const handleTopicToggle = (topicId: string, checked: boolean) => {
    setTopicProgress(prev => ({
      ...prev,
      [topicId]: checked
    }));
  };

  // Save progress mutation
  const saveProgressMutation = useMutation({
    mutationFn: async () => {
      const progressUpdates = Object.entries(topicProgress).map(([topicId, completed]) => ({
        studentId: selectedStudent,
        topicId,
        completed,
        completedDate: completed ? new Date().toISOString() : null,
      }));

      const token = localStorage.getItem('auth_token');
      return Promise.all(
        progressUpdates.map(update => 
          apiRequest('POST', '/api/progress', update)
        )
      );
    },
    onSuccess: () => {
      toast({
        title: 'Progress Saved',
        description: 'Student progress has been updated successfully.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/progress', selectedStudent] });
      setTopicProgress({});
    },
    onError: () => {
      toast({
        title: 'Error',
        description: 'Failed to save progress. Please try again.',
        variant: 'destructive',
      });
    },
  });

  const handleSaveProgress = () => {
    if (Object.keys(topicProgress).length === 0) {
      toast({
        title: 'No Changes',
        description: 'No progress changes to save.',
      });
      return;
    }
    saveProgressMutation.mutate();
  };

  // Get topic completion status
  const getTopicCompletion = (topicId: string) => {
    if (topicId in topicProgress) {
      return topicProgress[topicId];
    }
    const existing = existingProgress.find((p: any) => p.topicId === topicId);
    return existing?.completed || false;
  };

  // Get last updated date for topic
  const getLastUpdated = (topicId: string) => {
    const existing = existingProgress.find((p: any) => p.topicId === topicId);
    return existing?.completedDate ? new Date(existing.completedDate).toLocaleDateString() : '-';
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Progress Tracking</CardTitle>
        <p className="text-gray-600 mt-1">Update student topic completion status</p>
      </CardHeader>

      <CardContent>
        {/* Student Selection */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Select Student</label>
            <Select value={selectedStudent} onValueChange={setSelectedStudent}>
              <SelectTrigger>
                <SelectValue placeholder="Choose student..." />
              </SelectTrigger>
              <SelectContent>
                {students.map((student: any) => (
                  <SelectItem key={student.id} value={student.id}>
                    {student.name} - {student.classId}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Select Subject</label>
            <Select value={selectedSubject} onValueChange={setSelectedSubject}>
              <SelectTrigger>
                <SelectValue placeholder="Choose subject..." />
              </SelectTrigger>
              <SelectContent>
                {subjects.map((subject: any) => (
                  <SelectItem key={subject.id} value={subject.id}>
                    {subject.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Select Chapter</label>
            <Select value={selectedChapter} onValueChange={setSelectedChapter}>
              <SelectTrigger>
                <SelectValue placeholder="Choose chapter..." />
              </SelectTrigger>
              <SelectContent>
                {chapters.map((chapter: any) => (
                  <SelectItem key={chapter.id} value={chapter.id}>
                    {chapter.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>

        {/* Topic Checklist */}
        {selectedChapter && topics.length > 0 && (
          <div className="bg-gray-50 rounded-lg p-6">
            <h3 className="font-semibold text-gray-900 mb-4">
              Chapter: {chapters.find((c: any) => c.id === selectedChapter)?.name || 'Selected Chapter'}
            </h3>
            <div className="space-y-3">
              {topics.map((topic: any) => {
                const isCompleted = getTopicCompletion(topic.id);
                return (
                  <div
                    key={topic.id}
                    className="flex items-center justify-between p-4 bg-white rounded-lg border border-gray-200"
                  >
                    <div className="flex items-center space-x-3">
                      <Checkbox
                        checked={isCompleted}
                        onCheckedChange={(checked) => handleTopicToggle(topic.id, checked as boolean)}
                      />
                      <div>
                        <p className="font-medium text-gray-900">{topic.name}</p>
                        <p className="text-sm text-gray-600">{topic.description}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className={`px-2 py-1 text-xs font-medium rounded ${
                        isCompleted 
                          ? 'bg-success bg-opacity-10 text-success' 
                          : 'bg-gray-100 text-gray-600'
                      }`}>
                        {isCompleted ? 'Learned' : 'Pending'}
                      </span>
                      <span className="text-xs text-gray-500">
                        {getLastUpdated(topic.id)}
                      </span>
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="mt-6 flex justify-end space-x-3">
              <Button variant="outline">
                Cancel
              </Button>
              <Button 
                onClick={handleSaveProgress} 
                disabled={saveProgressMutation.isPending}
                className="bg-primary text-white hover:bg-blue-700"
              >
                <Save className="mr-2" size={16} />
                {saveProgressMutation.isPending ? 'Saving...' : 'Save Progress'}
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/qr/QRModal.tsx.content:
import { useState, useRef } from 'react';
import { Dialog, DialogContent } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { Download, Share2, QrCode, X } from 'lucide-react';
import { QRCodeSVG } from 'qrcode.react';
import navanidhiLogoPath from '@assets/navanidhi_1755015761485.png';

interface QRModalProps {
  isOpen: boolean;
  onClose: () => void;
  student: any;
}

export function QRModal({ isOpen, onClose, student }: QRModalProps) {
  const { toast } = useToast();
  const [isDownloading, setIsDownloading] = useState(false);
  const qrRef = useRef<any>(null);

  if (!student) return null;

  const progressUrl = `${window.location.origin}/progress/${student.qrCode}`;

  const handleDownload = async () => {
    setIsDownloading(true);
    try {
      if (qrRef.current) {
        const svg = qrRef.current.querySelector('svg');
        if (svg) {
          // Convert SVG to canvas for download
          const canvas = document.createElement('canvas');
          const ctx = canvas.getContext('2d');
          const img = new Image();

          const svgData = new XMLSerializer().serializeToString(svg);
          const svgBlob = new Blob([svgData], { type: 'image/svg+xml;charset=utf-8' });
          const url = URL.createObjectURL(svgBlob);

          img.onload = () => {
            canvas.width = 200;
            canvas.height = 200;
            ctx?.drawImage(img, 0, 0);

            const downloadUrl = canvas.toDataURL('image/png');
            const a = document.createElement('a');
            a.href = downloadUrl;
            a.download = `${student.name}-QR-Code.png`;
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);

            toast({
              title: 'QR Code Downloaded',
              description: 'QR code has been saved to your device.',
            });
          };

          img.src = url;
        }
      }
    } catch (error) {
      toast({
        title: 'Download Failed',
        description: 'Failed to download QR code. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsDownloading(false);
    }
  };

  const handleShare = async () => {
    try {
      if (navigator.share) {
        await navigator.share({
          title: `${student.name} - Progress Tracker`,
          text: `View ${student.name}'s academic progress in real-time`,
          url: progressUrl,
        });
      } else {
        // Fallback: copy to clipboard
        await navigator.clipboard.writeText(progressUrl);
        toast({
          title: 'Link Copied',
          description: 'Progress link has been copied to clipboard.',
        });
      }
    } catch (error) {
      toast({
        title: 'Share Failed',
        description: 'Failed to share QR code. Please try again.',
        variant: 'destructive',
      });
    }
  };

  const handleClose = () => {
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-lg w-full mx-4 p-0 animate-scale-in" onClick={(e) => e.stopPropagation()}>
        <Card className="border-0 shadow-2xl green-glow">
          <div className="p-8">
            {/* Header with Navanidhi Branding */}
            <div className="text-center mb-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-3 flex-1">
                  <img 
                    src={navanidhiLogoPath} 
                    alt="Navanidhi Academy" 
                    className="h-12 w-12 object-contain"
                  />
                  <div className="text-left">
                    <h3 className="text-xl font-bold text-green-800">Navanidhi Academy</h3>
                    <p className="text-sm text-green-600">Student Progress QR Code</p>
                  </div>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleClose}
                  className="h-8 w-8 p-0 hover:bg-green-50"
                >
                  <X size={16} className="text-green-600" />
                </Button>
              </div>

              {/* Student Info */}
              <div className="bg-gradient-to-r from-green-50 to-emerald-50 p-3 rounded-lg">
                <p className="text-green-800 font-semibold text-lg">
                  {student.name}
                </p>
                <p className="text-green-600 text-sm">
                  Class ID: {student.classId}
                </p>
              </div>
            </div>

            {/* QR Code Display Area with Green Theme */}
            <div className="relative">
              <div className="w-64 h-64 mx-auto mb-6 bg-white rounded-xl flex items-center justify-center border-2 border-green-200 green-pulse shadow-lg">
                <div ref={qrRef} className="p-4">
                  <QRCodeSVG
                    value={progressUrl}
                    size={200}
                    bgColor="#ffffff"
                    fgColor="#16a34a"
                    level="H"
                    includeMargin={true}
                  />
                </div>
              </div>

              {/* QR Code Info Footer */}
              <div className="text-center mb-4">
                <p className="text-green-700 font-medium text-sm">Scan for Real-time Progress</p>
                <p className="text-green-600 text-xs">High Quality QR Code</p>
              </div>
            </div>

            {/* Description */}
            <p className="text-sm text-green-700 mb-6 text-center bg-green-50 p-3 rounded-lg">
              Parents can scan this QR code to view real-time progress updates without needing to log in
            </p>

            {/* Action Buttons with Green Theme */}
            <div className="grid grid-cols-2 gap-3">
              <Button
                variant="outline"
                onClick={handleDownload}
                disabled={isDownloading}
                className="flex items-center justify-center border-green-300 text-green-700 hover:bg-green-50 hover-lift"
              >
                <Download className="mr-2" size={16} />
                {isDownloading ? 'Downloading...' : 'Download'}
              </Button>
              <Button
                onClick={handleShare}
                className="flex items-center justify-center green-gradient text-white hover:opacity-90 hover-lift"
              >
                <Share2 className="mr-2" size={16} />
                Share
              </Button>
            </div>

            {/* Additional Info with Green Theme */}
            <div className="mt-6 p-3 bg-green-50 rounded-lg border border-green-200">
              <p className="text-xs text-green-700 text-center font-medium">
                ðŸ’¡ Tip: Print this QR code and give it to parents for easy access to progress updates
              </p>
            </div>
          </div>
        </Card>
      </DialogContent>
    </Dialog>
  );
}

