    const count = 200;
    const defaults = {
      origin: { y: 0.7 },
      disableForReducedMotion: false // Ensure confetti shows but with no sound
    };

    // Left side burst
    confetti({
      ...defaults,
      particleCount: Math.floor(count * 0.25),
      angle: 60,
      spread: 55,
      origin: { x: 0 }
    });

    // Right side burst  
    confetti({
      ...defaults,
      particleCount: Math.floor(count * 0.25),
      angle: 120,
      spread: 55,
      origin: { x: 1 }
    });

    // Center burst
    confetti({
      ...defaults,
      particleCount: Math.floor(count * 0.35),
      angle: 90,
      spread: 100,
      origin: { x: 0.5 }
    });

    // Delayed second wave for extended celebration
    setTimeout(() => {
      confetti({
        particleCount: 100,
        spread: 120,
        origin: { y: 0.6 },
        disableForReducedMotion: false
      });
