          studentData,
          siblings,
          admissionFeePaid,
          receiptNumber
        });
        console.log('Registration response:', response.status, response.statusText);
        if (!response.ok) {
          throw new Error(`Registration failed: ${response.status} ${response.statusText}`);
        }
        return response;
      } catch (error) {
        console.error('Registration error:', error);
        throw error;
      }
    },
