                  <GraduationCap className="h-4 w-4" />
                  Academic Information & Fees
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="classId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Class *</FormLabel>
                        <FormControl>
                          <Select onValueChange={(value) => { field.onChange(value); handleClassChange(value); }} value={field.value}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select class" />
                            </SelectTrigger>
                            <SelectContent>
                              {classes.map((cls) => (
                                <SelectItem key={cls.value} value={cls.value}>
                                  {cls.label}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="courseType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Course Type *</FormLabel>
                        <FormControl>
                          <Select onValueChange={(value) => { field.onChange(value); handleCourseTypeChange(value); }} value={field.value}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select course type" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="monthly">Monthly Fee</SelectItem>
                              <SelectItem value="yearly">Yearly Fee</SelectItem>
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Enrollment Date */}
                <FormField
                  control={form.control}
                  name="enrollmentDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Enrollment Date *</FormLabel>
                      <FormControl>
                        <Input 
                          type="date" 
                          {...field}
                          max={new Date().toISOString().split('T')[0]} // Can't enroll for future dates
                        />
                      </FormControl>
                      <FormMessage />
                      <p className="text-xs text-gray-500 mt-1">
                        Select the enrollment date for the student
                      </p>
                    </FormItem>
                  )}
                />

                {/* Fee Display */}
                {isLoadingFees && selectedClass && selectedCourseType && (
                  <div className="p-4 bg-gray-50 rounded-lg border border-gray-200">
                    <div className="flex items-center gap-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                      <span className="text-gray-600">Loading fee structure...</span>
                    </div>
                  </div>
                )}
                {classFeesData && !isLoadingFees && (
                  <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                    <h4 className="font-medium text-blue-900 mb-3">ðŸ“Š Fee Structure for Selected Class</h4>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Admission Fee:</span>
                        <span className="font-semibold text-green-600 ml-2">â‚¹{classFeesData.admissionFee}</span>
                      </div>
                      {selectedCourseType === 'monthly' && classFeesData.monthlyFee && (
                        <div>
                          <span className="text-gray-600">Monthly Fee:</span>
                          <span className="font-semibold text-blue-600 ml-2">â‚¹{classFeesData.monthlyFee}</span>
                        </div>
                      )}
                      {selectedCourseType === 'yearly' && classFeesData.yearlyFee && (
                        <div>
                          <span className="text-gray-600">Yearly Fee:</span>
                          <span className="font-semibold text-purple-600 ml-2">â‚¹{classFeesData.yearlyFee}</span>
                        </div>
                      )}
                    </div>

                    {classFeesData.description && (
                      <p className="text-xs text-gray-600 mt-2">{classFeesData.description}</p>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Siblings Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Users className="h-4 w-4" />
                  Siblings Information
                  <Button type="button" onClick={addSibling} size="sm" variant="outline">
                    <Plus className="h-4 w-4 mr-1" />
                    Add Sibling
                  </Button>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {siblingFields.length === 0 ? (
                  <p className="text-gray-500 text-sm">No siblings added. Click "Add Sibling" to add sibling information.</p>
                ) : (
                  siblingFields.map((field, index) => (
                    <div key={field.id} className="p-4 border border-gray-200 rounded-lg bg-gray-50">
                      <div className="flex justify-between items-center mb-3">
                        <h5 className="font-medium text-gray-800">Sibling {index + 1}</h5>
                        <Button 
                          type="button" 
                          onClick={() => removeSibling(index)}
                          size="sm" 
                          variant="destructive"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name={`siblings.${index}.name`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Sibling Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Enter sibling name" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`siblings.${index}.className`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Class</FormLabel>
                              <FormControl>
                                <Input placeholder="e.g., 5th Class, 8th Class" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="grid grid-cols-2 gap-4 mt-3">
                        <FormField
                          control={form.control}
                          name={`siblings.${index}.schoolName`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>School Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Enter school name" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`siblings.${index}.schoolType`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>School Type</FormLabel>
                              <FormControl>
                                <RadioGroup
                                  onValueChange={field.onChange}
                                  value={field.value}
                                  className="flex space-x-4"
                                >
                                  <div className="flex items-center space-x-2">
                                    <RadioGroupItem value="government" id={`gov-${index}`} />
                                    <Label htmlFor={`gov-${index}`}>Government</Label>
                                  </div>
                                  <div className="flex items-center space-x-2">
                                    <RadioGroupItem value="private" id={`pvt-${index}`} />
                                    <Label htmlFor={`pvt-${index}`}>Private</Label>
                                  </div>
                                </RadioGroup>
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                    </div>
                  ))
                )}
              </CardContent>
            </Card>

            {/* Admission Fee Handling */}
            {classFeesData && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CreditCard className="h-4 w-4" />
                    Admission Fee Payment
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <FormField
                    control={form.control}
                    name="admissionFeePaid"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                        <FormControl>
                          <Checkbox
                            checked={field.value}
                            onCheckedChange={field.onChange}
                          />
                        </FormControl>
                        <div className="space-y-1 leading-none">
                          <FormLabel className="font-medium">
                            Admission Fee Paid (â‚¹{classFeesData.admissionFee})
                          </FormLabel>
                          <p className="text-sm text-gray-600">
                            Check this if the admission fee has been paid. The amount will be added to SO Center wallet.
                          </p>
                        </div>
                      </FormItem>
                    )}
                  />

                  {form.watch('admissionFeePaid') && (
                    <FormField
                      control={form.control}
                      name="receiptNumber"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Receipt Number *</FormLabel>
                          <FormControl>
                            <Input placeholder="Enter receipt/invoice number" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  )}
                </CardContent>
              </Card>
            )}



            <div className="flex justify-end space-x-3 pt-6">
              <Button type="button" variant="outline" onClick={onClose}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={createStudentMutation.isPending}
                className="bg-primary text-white hover:bg-blue-700"
                onClick={() => console.log('Register button clicked')}
              >
                {createStudentMutation.isPending ? (
                  'Registering Student...'
                ) : (
                  'Register Student'
                )}
              </Button>


            </div>
          </form>
        </Form>
      </DialogContent>

      {/* Success Screen */}
      <SuccessScreen 
        isOpen={showSuccessScreen}
        onClose={handleSuccessClose}
        studentData={registrationResult}
      />
    </Dialog>
  );
}

client/src/components/students/StudentTa.content:
import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { QRModal } from '@/components/qr/QRModal';
import { AddStudentModal } from '@/components/students/AddStudentModal';
import { 
  Search, 
  QrCode, 
  Edit, 
  TrendingUp, 
  IndianRupee,
  ChevronLeft,
  ChevronRight,
  Eye,
  UserPlus
} from 'lucide-react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useQuery } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';

interface Student {
  id: string;
  name: string;
  classId: string;
  parentPhone: string;
  qrCode: string;
}

interface StudentTableProps {
  students: Student[];
  isLoading: boolean;
}

export function StudentTable({ students, isLoading }: StudentTableProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [classFilter, setClassFilter] = useState('all');
  const [selectedStudent, setSelectedStudent] = useState<Student | null>(null);
  const [isQRModalOpen, setIsQRModalOpen] = useState(false);
  const [paymentHistory, setPaymentHistory] = useState<any[]>([]);
  const [isPaymentHistoryOpen, setIsPaymentHistoryOpen] = useState(false);
  const [loadingHistory, setLoadingHistory] = useState(false);
  const [isEnrollModalOpen, setIsEnrollModalOpen] = useState(false);
  const { toast } = useToast();
  const { user } = useAuth();

  // Fetch all classes dynamically
  const { data: classes = [] } = useQuery({
    queryKey: ['/api/classes'],
    queryFn: async () => {
      const response = await fetch('/api/classes', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch classes');
      }
      const data = await response.json();
      return Array.isArray(data) ? data : [];
    },
    refetchOnWindowFocus: false,
  });

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  // Filter students based on search and class filter
  const filteredStudents = (students || []).filter((student: any) => {
    const matchesSearch = student.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         student.parentPhone?.includes(searchTerm);

    // Class filtering logic - use actual class IDs from database
    const matchesClass = classFilter === 'all' || student.classId === classFilter;

    return matchesSearch && matchesClass;
  });

  console.log('Students received:', students);
  console.log('Filtered students:', filteredStudents);

  // Mock additional data for students that might not come from API
  const displayStudents = filteredStudents.map((student: any) => ({
    ...student,
    paymentStatus: student.paymentStatus || 'pending', // Use real payment status
    progress: student.progress || 0, // Initial progress is 0
    qrCode: student.qrCode || `student_${student.id}` // Ensure QR code exists
  }));

  const handleShowQR = (student: any) => {
    setSelectedStudent(student);
    setIsQRModalOpen(true);
  };

  const handleShowPaymentHistory = async (student: any) => {
    setLoadingHistory(true);
    try {
      const response = await fetch(`/api/students/${student.id}/payments`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch payment history');
      }

      const history = await response.json();
      setPaymentHistory(Array.isArray(history) ? history : []);
      setSelectedStudent(student);
      setIsPaymentHistoryOpen(true);
    } catch (error) {
      console.error('Payment history error:', error);
      toast({
        title: "Error",
        description: "Failed to fetch payment history",
        variant: "destructive"
      });
    } finally {
      setLoadingHistory(false);
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-gray-600">Loading students...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-xl font-semibold text-gray-900">Recent Students</CardTitle>
            <div className="flex items-center space-x-3">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-3 text-gray-400" size={16} />
                <Input
                  placeholder="Search students..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 w-64"
                />
              </div>

              {/* Filter */}
              <Select value={classFilter} onValueChange={setClassFilter}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="All Classes" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Classes</SelectItem>
                  {Array.isArray(classes) && classes.map((cls: any) => (
                    <SelectItem key={cls.id} value={cls.id}>
                      {cls.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* Enroll Student Button - Only for SO Center users */}
              {user?.role === 'so_center' && (
                <Button
                  onClick={() => setIsEnrollModalOpen(true)}
                  className="bg-primary hover:bg-primary/90 text-white px-4 py-2 rounded-md transition-colors"
                >
                  <UserPlus className="mr-2" size={16} />
                  Enroll Student
                </Button>
              )}
            </div>
          </div>
        </CardHeader>

        <CardContent>
          <div className="overflow-x-auto">
            {displayStudents.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-gray-500">No students found</p>
              </div>
            ) : (
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Student
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Class
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Payment Status
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Progress
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      QR Code
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {displayStudents.map((student) => (
                  <tr key={student.id} className="hover:bg-gray-50 transition">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="w-10 h-10 bg-gradient-to-r from-blue-400 to-blue-600 rounded-full flex items-center justify-center">
                          <span className="text-white font-medium text-sm">
                            {getInitials(student.name)}
                          </span>
                        </div>
                        <div className="ml-4">
                          <div className="text-sm font-medium text-gray-900">{student.name}</div>
                          <div className="text-sm text-gray-500">{student.parentPhone}</div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <Badge variant="outline" className="text-blue-800 border-blue-200">
                        {student.className || student.classId}
                      </Badge>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <Badge 
                        variant={student.paymentStatus === 'paid' ? 'default' : 'secondary'}
                        className={student.paymentStatus === 'paid' 
                          ? 'bg-success text-white' 
                          : 'bg-warning text-white'
                        }
                      >
                        {student.paymentStatus === 'paid' ? 'Paid' : 'Pending'}
                      </Badge>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="w-24 bg-gray-200 rounded-full h-2 mr-3">
                          <div 
                            className="bg-success h-2 rounded-full" 
                            style={{width: `${student.progress}%`}}
                          ></div>
                        </div>
                        <span className="text-sm font-medium text-gray-900">{student.progress}%</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleShowQR(student)}
                        className="text-gray-700 hover:bg-gray-100"
                      >
                        <QrCode className="mr-2" size={16} />
                        View QR
                      </Button>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex space-x-2">
                        <Button 
                          variant="ghost" 
                          size="sm" 
                          onClick={() => handleShowPaymentHistory(student)}
                          disabled={loadingHistory}
                          title="View Payment History"
                        >
                          <Eye className="text-blue-500" size={16} />
                        </Button>
                        <Button variant="ghost" size="sm">
                          <TrendingUp className="text-accent" size={16} />
                        </Button>
                        <Button variant="ghost" size="sm">
                          <IndianRupee className="text-secondary" size={16} />
                        </Button>
                      </div>
                    </td>
                  </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>

          {/* Pagination */}
          <div className="flex items-center justify-between mt-6 pt-4 border-t border-gray-200">
            <p className="text-sm text-gray-700">
              Showing <span className="font-medium">1</span> to <span className="font-medium">2</span> of{' '}
              <span className="font-medium">156</span> students
            </p>
            <nav className="flex space-x-2">
              <Button variant="outline" size="sm">
                <ChevronLeft size={16} />
              </Button>
              <Button variant="outline" size="sm" className="bg-primary text-white">
                1
              </Button>
              <Button variant="outline" size="sm">
                2
              </Button>
              <Button variant="outline" size="sm">
                3
              </Button>
              <Button variant="outline" size="sm">
                <ChevronRight size={16} />
              </Button>
            </nav>
          </div>
        </CardContent>
      </Card>

      <QRModal
        isOpen={isQRModalOpen}
        onClose={() => setIsQRModalOpen(false)}
        student={selectedStudent}
      />

      {/* Student Enrollment Modal */}
      <AddStudentModal
        isOpen={isEnrollModalOpen}
        onClose={() => setIsEnrollModalOpen(false)}
      />

      {/* Payment History Modal */}
      <Dialog open={isPaymentHistoryOpen} onOpenChange={setIsPaymentHistoryOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Payment History - {selectedStudent?.name}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 max-h-96 overflow-y-auto">
            {paymentHistory.length === 0 ? (
              <p className="text-center text-gray-500 py-8">No payment history found</p>
            ) : (
              paymentHistory.map((payment: any, index: number) => (
                <div key={payment.id || index} className="flex justify-between items-center p-4 border rounded-lg">
                  <div>
                    <p className="font-semibold text-lg">â‚¹{parseFloat(payment.amount || '0').toLocaleString()}</p>
                    <p className="text-sm text-gray-600">{payment.description || 'Payment'}</p>
                    <p className="text-xs text-gray-500">
                      {new Date(payment.createdAt).toLocaleDateString()} - {new Date(payment.createdAt).toLocaleTimeString()}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm capitalize font-medium">{payment.paymentMethod || 'Cash'}</p>
                    {payment.month && <p className="text-xs text-gray-500">{payment.month} {payment.year}</p>}
                    <Badge variant="outline" className="mt-1">
                      {payment.paymentMethod === 'wallet' ? 'Wallet' : 'Cash'}
                    </Badge>
                  </div>
                </div>
              ))
            )}
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    />
  </AlertDialogPortal>
))
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName

const AlertDialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
