import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import DashboardLayout from '@/components/layout/DashboardLayout';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Textarea } from '@/components/ui/textarea';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { InvoiceGenerator } from '@/components/InvoiceGenerator';
import { 
  CheckCircle, 
  XCircle, 
  Clock, 
  Eye, 
  IndianRupee,
  FileText,
  User,
  Calendar,
  AlertTriangle,
  MessageSquare,
  Wallet,
  CreditCard
} from 'lucide-react';

// Form schemas
const paymentFormSchema = z.object({
  paymentMode: z.enum(['upi', 'voucher'], {
    required_error: 'Please select a payment mode',
  }),
  paymentDetails: z.string().optional(),
  notes: z.string().optional(),
}).refine((data) => {
  // Only require payment details for UPI transactions
  if (data.paymentMode === 'upi' && (!data.paymentDetails || data.paymentDetails.trim() === '')) {
    return false;
  }
  return true;
}, {
  message: "UPI Transaction ID is required for UPI payments",
  path: ["paymentDetails"],
});

type PaymentFormData = z.infer<typeof paymentFormSchema>;

interface WithdrawalApprovalModalProps {
  isOpen: boolean;
  onClose: () => void;
  withdrawal: any;
}

function WithdrawalApprovalModal({ isOpen, onClose, withdrawal }: WithdrawalApprovalModalProps) {
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [invoiceData, setInvoiceData] = useState<any>(null);
  const [showInvoice, setShowInvoice] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<PaymentFormData>({
    resolver: zodResolver(paymentFormSchema),
    defaultValues: {
      paymentMode: undefined,
      paymentDetails: '',
      notes: '',
    },
  });

  const approveMutation = useMutation({
    mutationFn: async (data: PaymentFormData) => {
      return apiRequest('POST', `/api/admin/withdrawal-requests/${withdrawal.id}/approve`, data);
    },
    onSuccess: (response) => {
      toast({
        title: 'Withdrawal Approved',
        description: `Payment issued successfully. Transaction ID: ${response.transactionId}`,
      });

      // Set invoice data for payment
      setInvoiceData({
        transactionId: response.transactionId,
        withdrawalId: withdrawal.withdrawal_id,
        amount: parseFloat(withdrawal.amount),
        paymentMode: response.paymentMode,
        paymentDetails: form.getValues('paymentDetails'),
        userEmail: withdrawal.user_email,
        userName: `${withdrawal.first_name || ''} ${withdrawal.last_name || ''}`.trim(),
        processedAt: new Date().toISOString(),
        type: 'withdrawal'
      });

      queryClient.invalidateQueries({ queryKey: ['/api/admin/withdrawal-requests'] });
      setShowPaymentForm(false);
      onClose();
      setShowInvoice(true);
    },
    onError: (error: any) => {
      toast({
        title: 'Approval Failed',
        description: error.message || 'Failed to approve withdrawal request.',
        variant: 'destructive',
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async (notes: string) => {
      return apiRequest('POST', `/api/admin/withdrawal-requests/${withdrawal.id}/reject`, { notes });
    },
    onSuccess: () => {
      toast({
        title: 'Withdrawal Rejected',
        description: 'The withdrawal request has been rejected.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/withdrawal-requests'] });
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to reject withdrawal request.',
        variant: 'destructive',
      });
    },
  });

  if (!withdrawal) return null;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const onSubmit = (data: PaymentFormData) => {
    // For voucher payments, set a default payment details message
    if (data.paymentMode === 'voucher' && !data.paymentDetails) {
      data.paymentDetails = 'Voucher payment processed';
    }
    approveMutation.mutate(data);
  };

  return (
    <>
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Wallet className="h-5 w-5" />
              Withdrawal Request Details
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-6">
            {/* Request Overview */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Withdrawal ID</label>
                <p className="text-sm font-mono font-medium">#{withdrawal.id.slice(-8)}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Status</label>
                <Badge 
                  variant={withdrawal.status === 'pending' ? 'secondary' : 
                          withdrawal.status === 'approved' ? 'default' : 'destructive'}
                >
                  {withdrawal.status}
                </Badge>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Requested By</label>
                <p className="text-sm">{withdrawal.user_email}</p>
                <p className="text-xs text-gray-500 capitalize">{withdrawal.user_role}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Amount</label>
                <p className="text-sm font-medium text-green-600 flex items-center">
                  <IndianRupee className="h-4 w-4" />
                  {parseFloat(withdrawal.amount).toLocaleString('en-IN')}
                </p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Date Requested</label>
                <p className="text-sm">{new Date(withdrawal.request_date).toLocaleDateString('en-IN')}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">User Name</label>
                <p className="text-sm">{withdrawal.user_name || 'N/A'}</p>
              </div>
            </div>

            {/* Payment Form */}
            {showPaymentForm && withdrawal.status === 'pending' && (
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="paymentMode"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center gap-2">
                          <CreditCard className="h-4 w-4" />
                          Payment Mode
                        </FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select payment mode" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="upi">UPI Payment</SelectItem>
                            <SelectItem value="voucher">Bank Voucher</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {form.watch('paymentMode') === 'voucher' ? (
                    <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                      <div className="flex items-center">
                        <CheckCircle className="h-5 w-5 text-blue-600 mr-2" />
                        <div>
                          <p className="text-sm font-medium text-blue-900">Voucher Payment Selected</p>
                          <p className="text-sm text-blue-700">Ready to process voucher payment for â‚¹{parseFloat(withdrawal.amount).toLocaleString('en-IN')}</p>
                        </div>
                      </div>
                    </div>
                  ) : (
                    <FormField
                      control={form.control}
                      name="paymentDetails"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>
                            UPI Transaction ID
                          </FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="Enter UPI transaction ID"
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  )}

                  <FormField
                    control={form.control}
                    name="notes"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Additional Notes (Optional)</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Enter any additional notes..."
                            className="resize-none"
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex gap-3 pt-4">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setShowPaymentForm(false)}
                      className="flex-1"
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      disabled={approveMutation.isPending}
                      className="flex-1 bg-green-600 hover:bg-green-700"
                    >
                      {approveMutation.isPending ? 'Processing...' : 'Confirm Payment'}
                    </Button>
                  </div>
                </form>
              </Form>
            )}

            {/* Action Buttons */}
            {!showPaymentForm && withdrawal.status === 'pending' && (
              <div className="flex gap-3 pt-4">
                <Button
                  onClick={() => rejectMutation.mutate('Request rejected by admin')}
                  variant="outline"
                  disabled={rejectMutation.isPending}
                  className="flex-1 text-red-600 border-red-200 hover:bg-red-50"
                >
                  <XCircle className="h-4 w-4 mr-2" />
                  Reject Request
                </Button>
                <Button
                  onClick={() => setShowPaymentForm(true)}
                  className="flex-1 bg-blue-600 hover:bg-blue-700"
                >
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Issue Payment
                </Button>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>

      {/* Invoice Generator */}
      <InvoiceGenerator 
        invoiceData={invoiceData}
        isOpen={showInvoice}
        onClose={() => setShowInvoice(false)}
      />
    </>
  );
}

export default function Approvals() {
  const [selectedStatus, setSelectedStatus] = useState<string>('pending');
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [selectedWithdrawal, setSelectedWithdrawal] = useState<any>(null);
  const [isDetailsModalOpen, setIsDetailsModalOpen] = useState(false);

  // Fetch real withdrawal requests from API
  const { data: withdrawalRequests = [], isLoading } = useQuery({
    queryKey: ['/api/admin/withdrawal-requests'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/admin/withdrawal-requests');
      const result = await response.json();
      return result;
    },
  });

  // Filter withdrawal requests based on status and search
  const filteredRequests = (Array.isArray(withdrawalRequests) ? withdrawalRequests : []).filter((request: any) => {
    const matchesStatus = selectedStatus === 'all' || request.status === selectedStatus;
    const matchesSearch = !searchTerm || 
      request.user_email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.id?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      request.user_name?.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesSearch;
  });

  const handleViewDetails = (withdrawal: any) => {
    setSelectedWithdrawal(withdrawal);
    setIsDetailsModalOpen(true);
  };

  const handleCloseDetailsModal = () => {
    setIsDetailsModalOpen(false);
    setSelectedWithdrawal(null);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'secondary';
      case 'approved': return 'default';
      case 'rejected': return 'destructive';
      default: return 'secondary';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending': return Clock;
      case 'approved': return CheckCircle;
      case 'rejected': return XCircle;
      default: return Clock;
    }
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-64">Loading withdrawal requests...</div>;
  }

  const pendingCount = filteredRequests.filter(w => w.status === 'pending').length;
  const approvedCount = filteredRequests.filter(w => w.status === 'approved').length;
  const rejectedCount = filteredRequests.filter(w => w.status === 'rejected').length;
  const totalAmount = filteredRequests.filter(w => w.amount && w.status === 'approved').reduce((sum, w) => sum + (parseFloat(w.amount) || 0), 0);

  return (
    <DashboardLayout title="Withdrawal Approvals" subtitle="Review and approve agent withdrawal requests">
      <div className="space-y-6">

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Withdrawals</CardTitle>
            <Clock className="h-4 w-4 text-yellow-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-yellow-600">{pendingCount}</div>
            <p className="text-xs text-muted-foreground">Awaiting approval</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Approved</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{approvedCount}</div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Rejected</CardTitle>
            <XCircle className="h-4 w-4 text-red-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-red-600">{rejectedCount}</div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Approved Amount</CardTitle>
            <IndianRupee className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-600">
              â‚¹{totalAmount.toLocaleString()}
            </div>
            <p className="text-xs text-muted-foreground">This month</p>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Filters</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="text-sm font-medium text-gray-700 mb-2 block">Search</label>
            <Input
              placeholder="Search by email, withdrawal ID, or name..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div>
            <label className="text-sm font-medium text-gray-700 mb-2 block">Status</label>
            <Select value={selectedStatus} onValueChange={setSelectedStatus}>
              <SelectTrigger>
                <SelectValue placeholder="Select status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div className="flex items-end">
            <Button 
              variant="outline" 
              onClick={() => {
                setSelectedStatus('pending');
                setSearchTerm('');
              }}
              className="w-full"
            >
              Clear Filters
            </Button>
          </div>
        </div>
      </div>

      {/* Withdrawal Requests Table */}
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">Withdrawal Requests</h2>
          <p className="text-sm text-gray-600">Review and process agent withdrawal requests</p>
        </div>

        {filteredRequests.length === 0 ? (
          <div className="text-center py-12">
            <Wallet className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No withdrawal requests</h3>
            <p className="text-gray-600">There are no withdrawal requests to display.</p>
          </div>
        ) : (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Withdrawal ID</TableHead>
                <TableHead>Requested By</TableHead>
                <TableHead>Amount</TableHead>
                <TableHead>Date Requested</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredRequests.map((request: any) => {
                const StatusIcon = getStatusIcon(request.status);
                return (
                  <TableRow key={request.id}>
                    <TableCell>
                      <div className="font-mono text-sm font-medium">#{request.id.slice(-8)}</div>
                    </TableCell>
                    <TableCell>
                      <div>
                        <div className="font-medium">{request.user_email}</div>
                        <div className="text-sm text-gray-600 capitalize">
                          {request.user_name || 'N/A'} 
                          {request.user_role && ` â€¢ ${request.user_role}`}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center space-x-1">
                        <IndianRupee className="w-4 h-4 text-green-600" />
                        <span className="font-medium">
                          {parseFloat(request.amount).toLocaleString('en-IN')}
                        </span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm">
                        <div>{new Date(request.request_date).toLocaleDateString('en-IN')}</div>
                        <div className="text-gray-600">{new Date(request.request_date).toLocaleTimeString('en-IN')}</div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center space-x-2">
                        <StatusIcon className="w-4 h-4" />
                        <Badge variant={getStatusColor(request.status)}>
                          {request.status}
                        </Badge>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex space-x-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleViewDetails(request)}
                        >
                          <Eye className="w-4 h-4 mr-1" />
                          {request.status === 'pending' ? 'Review' : 'View'}
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        )}
      </div>

        <WithdrawalApprovalModal
          isOpen={isDetailsModalOpen}
          onClose={handleCloseDetailsModal}
          withdrawal={selectedWithdrawal}
        />
      </div>
    </DashboardLayout>
  );
}

client/src/pages/admin/Attendance.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import DashboardLayout from '@/components/layout/DashboardLayout';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { CalendarIcon, Users, TrendingUp, TrendingDown, UserCheck, Download, MapPin, Building, School } from 'lucide-react';
import { format } from 'date-fns';
import { cn } from '@/lib/utils';

interface State {
  id: string;
  name: string;
}

interface District {
  id: string;
  name: string;
  stateId: string;
}

interface Mandal {
  id: string;
  name: string;
  districtId: string;
}

interface Village {
  id: string;
  name: string;
  mandalId: string;
}

interface SoCenter {
  id: string;
  name: string;
  villageId: string;
}

interface Class {
  id: string;
  name: string;
}

interface Student {
  id: string;
  name: string;
  student_id: string;
  class_name: string;
}

interface AttendanceRecord {
  id: string;
  student_id: string;
  date: string;
  status: 'present' | 'absent' | 'late';
  remarks?: string;
  student_name: string;
  student_code: string;
  class_name: string;
  center_name: string;
  center_code: string;
}

export default function Attendance() {
  // Hierarchical filter states
  const [selectedState, setSelectedState] = useState<string>('');
  const [selectedDistrict, setSelectedDistrict] = useState<string>('');
  const [selectedMandal, setSelectedMandal] = useState<string>('');
  const [selectedVillage, setSelectedVillage] = useState<string>('');
  const [selectedCenter, setSelectedCenter] = useState<string>('');
  const [selectedClass, setSelectedClass] = useState<string>('');
  const [selectedStudent, setSelectedStudent] = useState<string>('');
  const [selectedDate, setSelectedDate] = useState<string>(format(new Date(), 'yyyy-MM-dd'));

  // API calls for hierarchical data
  const { data: states = [] } = useQuery({
    queryKey: ['/api/locations/states'],
    enabled: true,
  });

  const { data: districts = [] } = useQuery({
    queryKey: ['/api/locations/districts', selectedState],
    queryFn: async () => {
      if (!selectedState) return [];
      const response = await fetch(`/api/locations/districts?stateId=${selectedState}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch districts');
      return response.json();
    },
    enabled: !!selectedState,
  });

  const { data: mandals = [] } = useQuery({
    queryKey: ['/api/locations/mandals', selectedDistrict],
    queryFn: async () => {
      if (!selectedDistrict) return [];
      const response = await fetch(`/api/locations/mandals?districtId=${selectedDistrict}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch mandals');
      return response.json();
    },
    enabled: !!selectedDistrict,
  });

  const { data: villages = [] } = useQuery({
    queryKey: ['/api/locations/villages', selectedMandal],
    queryFn: async () => {
      if (!selectedMandal) return [];
      const response = await fetch(`/api/locations/villages?mandalId=${selectedMandal}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch villages');
      return response.json();
    },
    enabled: !!selectedMandal,
  });

  const { data: centers = [] } = useQuery({
    queryKey: ['/api/locations/so-centers', selectedVillage],
    queryFn: async () => {
      if (!selectedVillage) return [];
      const response = await fetch(`/api/locations/so-centers?villageId=${selectedVillage}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch SO centers');
      return response.json();
    },
    enabled: !!selectedVillage,
  });

  const { data: classes = [] } = useQuery({
    queryKey: ['/api/classes/by-center', selectedCenter],
    queryFn: async () => {
      if (!selectedCenter) return [];
      const response = await fetch(`/api/classes/by-center?centerId=${selectedCenter}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch classes');
      return response.json();
    },
    enabled: !!selectedCenter,
  });

  const { data: students = [] } = useQuery({
    queryKey: ['/api/students/by-filter', selectedClass, selectedCenter],
    queryFn: async () => {
      if (!selectedClass && !selectedCenter) return [];
      const params = new URLSearchParams();
      if (selectedClass) params.append('classId', selectedClass);
      if (selectedCenter) params.append('centerId', selectedCenter);

      const response = await fetch(`/api/students/by-filter?${params.toString()}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch students');
      return response.json();
    },
    enabled: !!(selectedClass || selectedCenter),
  });

  // Attendance data query
  const { data: attendanceData = [], isLoading } = useQuery({
    queryKey: ['/api/attendance', selectedStudent, selectedClass, selectedCenter, selectedDate],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (selectedStudent) params.append('studentId', selectedStudent);
      if (selectedClass) params.append('classId', selectedClass);
      if (selectedCenter) params.append('centerId', selectedCenter);
      if (selectedDate) params.append('date', selectedDate);

      const response = await fetch(`/api/attendance?${params.toString()}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch attendance');
      return response.json();
    },
    enabled: !!(selectedStudent || selectedClass || selectedCenter),
  });

  // Reset child selections when parent changes
  useEffect(() => {
    setSelectedDistrict('');
    setSelectedMandal('');
    setSelectedVillage('');
    setSelectedCenter('');
    setSelectedClass('');
    setSelectedStudent('');
  }, [selectedState]);

  useEffect(() => {
    setSelectedMandal('');
    setSelectedVillage('');
    setSelectedCenter('');
    setSelectedClass('');
    setSelectedStudent('');
  }, [selectedDistrict]);

  useEffect(() => {
    setSelectedVillage('');
    setSelectedCenter('');
    setSelectedClass('');
    setSelectedStudent('');
  }, [selectedMandal]);

  useEffect(() => {
    setSelectedCenter('');
    setSelectedClass('');
    setSelectedStudent('');
  }, [selectedVillage]);

  useEffect(() => {
    setSelectedClass('');
    setSelectedStudent('');
  }, [selectedCenter]);

  useEffect(() => {
    setSelectedStudent('');
  }, [selectedClass]);

  // Calculate attendance statistics
  const totalPresent = attendanceData.filter((record: AttendanceRecord) => record.status === 'present').length;
  const totalAbsent = attendanceData.filter((record: AttendanceRecord) => record.status === 'absent').length;
  const totalLate = attendanceData.filter((record: AttendanceRecord) => record.status === 'late').length;
  const attendancePercentage = attendanceData.length > 0 ? Math.round((totalPresent / attendanceData.length) * 100) : 0;

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case 'present': return 'default';
      case 'absent': return 'destructive';
      case 'late': return 'secondary';
      default: return 'outline';
    }
  };

  return (
    <DashboardLayout>
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Attendance Monitoring</h1>
            <p className="text-muted-foreground">
              Track student attendance with hierarchical filtering across the academy
            </p>
          </div>
          <Button variant="outline" className="gap-2">
            <Download className="h-4 w-4" />
            Export Report
          </Button>
        </div>

        {/* Hierarchical Filters */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5" />
              Location & Student Filters
            </CardTitle>
            <p className="text-sm text-muted-foreground">
              Select filters from left to right to narrow down attendance data
            </p>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Date Filter */}
              <div className="space-y-2">
                <Label>Date</Label>
                <Input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="w-full md:w-64"
                />
              </div>

              {/* Location Hierarchy Filters */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-7 gap-4 overflow-x-auto">
                {/* State */}
                <div className="space-y-2 min-w-40">
                  <Label>State</Label>
                  <Select value={selectedState} onValueChange={setSelectedState}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select State" />
                    </SelectTrigger>
                    <SelectContent>
                      {(states as State[]).map((state: State) => (
                        <SelectItem key={state.id} value={state.id}>
                          {state.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* District */}
                <div className="space-y-2 min-w-40">
                  <Label>District</Label>
                  <Select 
                    value={selectedDistrict} 
                    onValueChange={setSelectedDistrict}
                    disabled={!selectedState}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select District" />
                    </SelectTrigger>
                    <SelectContent>
                      {(districts as District[]).map((district: District) => (
                        <SelectItem key={district.id} value={district.id}>
                          {district.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Mandal */}
                <div className="space-y-2 min-w-40">
                  <Label>Mandal</Label>
                  <Select 
                    value={selectedMandal} 
                    onValueChange={setSelectedMandal}
                    disabled={!selectedDistrict}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select Mandal" />
                    </SelectTrigger>
                    <SelectContent>
                      {(mandals as Mandal[]).map((mandal: Mandal) => (
                        <SelectItem key={mandal.id} value={mandal.id}>
                          {mandal.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Village */}
                <div className="space-y-2 min-w-40">
                  <Label>Village</Label>
                  <Select 
                    value={selectedVillage} 
                    onValueChange={setSelectedVillage}
                    disabled={!selectedMandal}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select Village" />
                    </SelectTrigger>
                    <SelectContent>
                      {(villages as Village[]).map((village: Village) => (
                        <SelectItem key={village.id} value={village.id}>
                          {village.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* SO Center */}
                <div className="space-y-2 min-w-40">
                  <Label>SO Center</Label>
                  <Select 
                    value={selectedCenter} 
                    onValueChange={setSelectedCenter}
                    disabled={!selectedVillage}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select SO Center" />
                    </SelectTrigger>
                    <SelectContent>
                      {(centers as SoCenter[]).map((center: SoCenter) => (
                        <SelectItem key={center.id} value={center.id}>
                          {center.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Class */}
                <div className="space-y-2 min-w-40">
                  <Label>Class</Label>
                  <Select 
                    value={selectedClass} 
                    onValueChange={setSelectedClass}
                    disabled={!selectedCenter}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select Class" />
                    </SelectTrigger>
                    <SelectContent>
                      {(classes as Class[]).map((cls: Class) => (
                        <SelectItem key={cls.id} value={cls.id}>
                          {cls.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Student */}
                <div className="space-y-2 min-w-40">
                  <Label>Student (Optional)</Label>
                  <Select 
                    value={selectedStudent} 
                    onValueChange={setSelectedStudent}
                    disabled={!selectedClass}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select Student" />
                    </SelectTrigger>
                    <SelectContent>
                      {(students as Student[]).map((student: Student) => (
                        <SelectItem key={student.id} value={student.id}>
                          {student.name} ({student.student_id})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Attendance Statistics */}
        {attendanceData.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-2xl font-bold text-green-600">{totalPresent}</p>
                    <p className="text-xs text-muted-foreground">Present</p>
                  </div>
                  <UserCheck className="h-4 w-4 text-green-600" />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-2xl font-bold text-red-600">{totalAbsent}</p>
                    <p className="text-xs text-muted-foreground">Absent</p>
                  </div>
                  <TrendingDown className="h-4 w-4 text-red-600" />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-2xl font-bold text-yellow-600">{totalLate}</p>
                    <p className="text-xs text-muted-foreground">Late</p>
                  </div>
                  <TrendingUp className="h-4 w-4 text-yellow-600" />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-2xl font-bold">{attendancePercentage}%</p>
                    <p className="text-xs text-muted-foreground">Attendance Rate</p>
                  </div>
                  <Users className="h-4 w-4" />
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Attendance Table */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <School className="h-5 w-5" />
              Attendance Records
            </CardTitle>
            <p className="text-sm text-muted-foreground">
              {selectedStudent 
                ? `Showing attendance for selected student on ${format(new Date(selectedDate), 'MMM dd, yyyy')}`
                : `Showing attendance records based on current filters for ${format(new Date(selectedDate), 'MMM dd, yyyy')}`
              }
            </p>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center p-8">
                <div className="text-muted-foreground">Loading attendance data...</div>
              </div>
            ) : attendanceData.length === 0 ? (
              <div className="flex items-center justify-center p-8">
                <div className="text-center text-muted-foreground">
                  <School className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p className="text-lg font-medium">No attendance records found</p>
                  <p className="text-sm">
                    {!selectedCenter 
                      ? "Please select filters to view attendance data"
                      : "No attendance records for the selected filters and date"
                    }
                  </p>
                </div>
              </div>
            ) : (
              <div className="rounded-md border">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Student</TableHead>
                      <TableHead>Student ID</TableHead>
                      <TableHead>Class</TableHead>
                      <TableHead>SO Center</TableHead>
                      <TableHead>Date</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Remarks</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {attendanceData.map((record: AttendanceRecord) => (
                      <TableRow key={record.id}>
                        <TableCell className="font-medium">
                          {record.student_name}
                        </TableCell>
                        <TableCell>
                          <Badge variant="outline">{record.student_code}</Badge>
                        </TableCell>
                        <TableCell>{record.class_name}</TableCell>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            <Building className="h-4 w-4 text-muted-foreground" />
                            {record.center_name}
                          </div>
                        </TableCell>
                        <TableCell>
                          {format(new Date(record.date), 'MMM dd, yyyy')}
                        </TableCell>
                        <TableCell>
                          <Badge variant={getStatusBadgeVariant(record.status)}>
                            {record.status.charAt(0).toUpperCase() + record.status.slice(1)}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {record.remarks || '-'}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/admin/Centers.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import DashboardLayout from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { AddSoCenterModal } from '@/components/admin/AddSoCenterModal';
import { EditSoCenterModal } from '@/components/admin/EditSoCenterModal';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { 
  Search, 
  Building, 
  Plus,
  Edit,
  Eye,
  MapPin,
  Phone,
  Users,
  Trash2
} from 'lucide-react';

export default function AdminCenters() {
  const [searchTerm, setSearchTerm] = useState('');
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [selectedCenter, setSelectedCenter] = useState<any>(null);
  const [editingCenter, setEditingCenter] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch real SO Centers data from API
  const { data: centers = [], isLoading, refetch: refetchCenters } = useQuery({
    queryKey: ['/api/admin/so-centers'],
  });

  // Delete center mutation (This mutation is not used in the handleDeleteCenter, but kept for completeness)
  const deleteCenterMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest('DELETE', `/api/admin/so-centers/${id}`);
    },
    onSuccess: () => {
      toast({
        title: 'Center Deleted',
        description: 'SO Center has been successfully deleted.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/so-centers'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete SO center.',
        variant: 'destructive',
      });
    },
  });

  const filteredCenters = (centers as any[]).filter(center => 
    center.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    center.address.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (center.managerName || '').toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const handleAddCenter = () => {
    setIsAddModalOpen(true);
  };

  const handleEditCenter = (center: any) => {
    setEditingCenter(center);
  };

  // Updated handleDeleteCenter function
  const handleDeleteCenter = async (centerId: string) => {
    const center = centers.find(c => c.id === centerId);
    const centerName = center?.name || 'this SO Center';

    if (!confirm(`Are you sure you want to permanently delete "${centerName}"?\n\nThis action cannot be undone and will:\n- Remove all SO Center data\n- Delete associated user account\n- Remove related records\n\nNote: SO Centers with active students cannot be deleted.`)) {
      return;
    }

    try {
      const response = await fetch(`/api/admin/so-centers/${centerId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        toast({
          title: "Success",
          description: `SO Center "${centerName}" deleted successfully`,
        });
        refetchCenters();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete SO Center');
      }
    } catch (error: any) {
      console.error('Error deleting SO Center:', error);
      toast({
        title: "Delete Failed",
        description: error.message || "Failed to delete SO Center",
        variant: "destructive",
      });
    }
  };

  return (
    <DashboardLayout
      title="SO Centers"
      subtitle="Manage satellite office centers and their operations"
      showAddButton={true}
      onAddClick={handleAddCenter}
    >
      <div className="space-y-6">
        {/* Search and Stats */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="text-xl font-semibold text-gray-900">SO Centers Overview</CardTitle>
              <div className="relative">
                <Search className="absolute left-3 top-3 text-gray-400" size={16} />
                <Input
                  placeholder="Search centers..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 w-64"
                />
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center p-4 bg-blue-50 rounded-lg">
                <div className="text-2xl font-bold text-blue-600">{(centers as any[]).length}</div>
                <p className="text-gray-600">Total Centers</p>
              </div>
              <div className="text-center p-4 bg-green-50 rounded-lg">
                <div className="text-2xl font-bold text-green-600">
                  {(centers as any[]).reduce((sum, center) => sum + (center.studentCount || 0), 0)}
                </div>
                <p className="text-gray-600">Total Students</p>
              </div>
              <div className="text-center p-4 bg-purple-50 rounded-lg">
                <div className="text-2xl font-bold text-purple-600">
                  â‚¹{(centers as any[]).reduce((sum, center) => sum + (parseInt(center.walletBalance) || 0), 0).toLocaleString()}
                </div>
                <p className="text-gray-600">Total Wallet Balance</p>
              </div>
              <div className="text-center p-4 bg-yellow-50 rounded-lg">
                <div className="text-2xl font-bold text-yellow-600">
                  {(centers as any[]).filter(c => c.isActive).length}
                </div>
                <p className="text-gray-600">Active Centers</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Centers List */}
        {isLoading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-gray-500">Loading SO centers...</p>
          </div>
        ) : (
          <div className="grid gap-6">
            {filteredCenters.map((center) => (
              <Card key={center.id}>
                <CardContent className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex items-start space-x-4">
                      <div className="w-12 h-12 bg-gradient-to-r from-blue-400 to-blue-600 rounded-lg flex items-center justify-center">
                        <Building className="text-white" size={20} />
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center space-x-3 mb-2">
                          <h3 className="text-lg font-semibold text-gray-900">{center.name}</h3>
                          <Badge className={center.isActive ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"}>
                            {center.isActive ? 'ACTIVE' : 'INACTIVE'}
                          </Badge>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-600">
                          <div className="flex items-center space-x-2">
                            <MapPin size={16} className="text-gray-400" />
                            <span>{center.address}</span>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Phone size={16} className="text-gray-400" />
                            <span>{center.phone}</span>
                          </div>
                          <div className="flex items-center space-x-2">
                            <div className="w-6 h-6 bg-gradient-to-r from-green-400 to-green-600 rounded-full flex items-center justify-center">
                              <span className="text-white text-xs font-medium">
                                {getInitials(center.managerName || 'Unknown')}
                              </span>
                            </div>
                            <span>{center.managerName || 'Unknown'} - {center.managerEmail || 'N/A'}</span>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Users size={16} className="text-gray-400" />
                            <span>{center.studentCount || 0} students</span>
                          </div>
                        </div>

                        <div className="mt-4 flex items-center space-x-6 text-sm">
                          <div>
                            <span className="text-gray-500">Wallet Balance: </span>
                            <span className="font-semibold text-green-600">â‚¹{(parseInt(center.walletBalance) || 0).toLocaleString()}</span>
                          </div>
                          <div>
                            <span className="text-gray-500">Created: </span>
                            <span className="font-medium">{new Date(center.createdAt).toLocaleDateString()}</span>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="flex space-x-2">
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={() => setSelectedCenter(center)}
                        className="hover:bg-blue-50"
                      >
                        <Eye className="text-blue-600" size={16} />
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => handleEditCenter(center)}
                        className="hover:bg-green-50"
                      >
                        <Edit className="text-green-600" size={16} />
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => handleDeleteCenter(center.id)}
                        disabled={deleteCenterMutation.isPending}
                        className="hover:bg-red-50"
                      >
                        <Trash2 className="text-red-600" size={16} />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}

            {filteredCenters.length === 0 && (
              <Card>
                <CardContent className="p-8 text-center">
                  <Building className="mx-auto text-gray-400 mb-4" size={48} />
                  <p className="text-gray-500">No SO centers found matching your search.</p>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </div>

      <AddSoCenterModal 
        isOpen={isAddModalOpen} 
        onClose={() => setIsAddModalOpen(false)}
        onSuccess={() => {
          queryClient.invalidateQueries({ queryKey: ['/api/admin/so-centers'] });
        }}
      />

      <EditSoCenterModal
        isOpen={!!editingCenter}
        onClose={() => setEditingCenter(null)}
        center={editingCenter}
      />

      {/* View Details Dialog */}
      <Dialog open={!!selectedCenter} onOpenChange={() => setSelectedCenter(null)}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>SO Center Details</DialogTitle>
            <DialogDescription>
              Complete information about {selectedCenter?.name}
            </DialogDescription>
          </DialogHeader>

          {selectedCenter && (
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-6">
                <div>
                  <h3 className="font-semibold mb-3">Basic Information</h3>
                  <div className="space-y-2 text-sm">
                    <p><span className="font-medium">Center ID:</span> {selectedCenter.centerId || selectedCenter.center_id || 'N/A'}</p>
                    <p><span className="font-medium">Name:</span> {selectedCenter.name || 'N/A'}</p>
                    <p><span className="font-medium">Email:</span> {selectedCenter.email || 'N/A'}</p>
                    <p><span className="font-medium">Phone:</span> {selectedCenter.phone || 'N/A'}</p>
                    <p><span className="font-medium">Address:</span> {selectedCenter.address || 'N/A'}</p>
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold mb-3">Financial Information</h3>
                  <div className="space-y-2 text-sm">
                    <p><span className="font-medium">Wallet Balance:</span> â‚¹{(parseFloat(selectedCenter.walletBalance || selectedCenter.wallet_balance) || 0).toLocaleString()}</p>
                    {selectedCenter.rentAmount && (
                      <p><span className="font-medium">Rent Amount:</span> â‚¹{(parseFloat(selectedCenter.rentAmount || selectedCenter.rent_amount) || 0).toLocaleString()}</p>
                    )}
                    {selectedCenter.electricBillAccountNumber && (
                      <p><span className="font-medium">Electric Bill Account:</span> {selectedCenter.electricBillAccountNumber || selectedCenter.electric_bill_account_number || 'N/A'}</p>
                    )}
                    {selectedCenter.internetBillAccountNumber && (
                      <p><span className="font-medium">Internet Bill Account:</span> {selectedCenter.internetBillAccountNumber || selectedCenter.internet_bill_account_number || 'N/A'}</p>
                    )}
                  </div>
                </div>
              </div>

              {(selectedCenter.ownerName || selectedCenter.owner_name) && (
                <div>
                  <h3 className="font-semibold mb-3">Property Owner Information</h3>
                  <div className="grid grid-cols-2 gap-6 text-sm">
                    <div className="space-y-2">
                      <p><span className="font-medium">Owner Name:</span> {(selectedCenter.ownerName || selectedCenter.owner_name || '')} {(selectedCenter.ownerLastName || selectedCenter.owner_last_name || '')}</p>
                      {selectedCenter.ownerFatherName && (
                        <p><span className="font-medium">Father's Name:</span> {selectedCenter.ownerFatherName || selectedCenter.owner_father_name}</p>
                      )}
                      {selectedCenter.ownerMotherName && (
                        <p><span className="font-medium">Mother's Name:</span> {selectedCenter.ownerMotherName || selectedCenter.owner_mother_name}</p>
                      )}
                    </div>
                    <div className="space-y-2">
                      {selectedCenter.ownerPhone && (
                        <p><span className="font-medium">Owner Phone:</span> {selectedCenter.ownerPhone || selectedCenter.owner_phone}</p>
                      )}
                      {selectedCenter.roomSize && (
                        <p><span className="font-medium">Room Size:</span> {selectedCenter.roomSize || selectedCenter.room_size}</p>
                      )}
                      {selectedCenter.landmarks && (
                        <p><span className="font-medium">Landmarks:</span> {selectedCenter.landmarks}</p>
                      )}
                    </div>
                  </div>
                </div>
              )}

              <div>
                <h3 className="font-semibold mb-3">Status & Statistics</h3>
                <div className="grid grid-cols-3 gap-6 text-sm">
                  <div className="space-y-2">
                    <div className="flex items-center">
                      <span className="font-medium mr-2">Status:</span> 
                      <Badge className={(selectedCenter.isActive !== false && selectedCenter.is_active !== false) ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"}>
                        {(selectedCenter.isActive !== false && selectedCenter.is_active !== false) ? 'ACTIVE' : 'INACTIVE'}
                      </Badge>
                    </div>
                    <p><span className="font-medium">Students:</span> {selectedCenter.studentCount || selectedCenter.student_count || 0}</p>
                  </div>
                  <div className="space-y-2">
                    {selectedCenter.capacity && (
                      <p><span className="font-medium">Capacity:</span> {selectedCenter.capacity || 'N/A'}</p>
                    )}
                    <p><span className="font-medium">Created:</span> {new Date(selectedCenter.createdAt || selectedCenter.created_at).toLocaleDateString()}</p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </DashboardLayout>
  );
}

