import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import DashboardLayout from '@/components/layout/DashboardLayout';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Plus, Edit, Trash2, IndianRupee, Calendar } from 'lucide-react';

const feeSchema = z.object({
  classId: z.string().min(1, 'Class selection is required'),
  courseType: z.enum(['monthly', 'yearly'], { required_error: 'Course type is required' }),
  monthlyFee: z.string().min(1, 'Monthly fee is required'),
  yearlyFee: z.string().min(1, 'Yearly fee is required'),
  admissionFee: z.string().min(1, 'Admission fee is required'),
  description: z.string().optional(),
  isActive: z.boolean().default(true),
});

type FeeFormData = z.infer<typeof feeSchema>;

interface AddEditFeeModalProps {
  isOpen: boolean;
  onClose: () => void;
  editingFee?: any;
}

function AddEditFeeModal({ isOpen, onClose, editingFee }: AddEditFeeModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch real classes data from API
  const { data: classes = [] } = useQuery({
    queryKey: ['/api/classes'],
  });

  const form = useForm<FeeFormData>({
    resolver: zodResolver(feeSchema),
    defaultValues: {
      classId: editingFee?.classId || '',
      courseType: editingFee?.courseType || 'monthly',
      monthlyFee: editingFee?.monthlyFee?.toString() || '',
      yearlyFee: editingFee?.yearlyFee?.toString() || '',
      admissionFee: editingFee?.admissionFee?.toString() || '',
      description: editingFee?.description || '',
      isActive: editingFee?.isActive ?? true,
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: FeeFormData) => {
      const submitData = {
        ...data,
        monthlyFee: parseInt(data.monthlyFee),
        yearlyFee: parseInt(data.yearlyFee),
        admissionFee: parseInt(data.admissionFee),
      };
      const endpoint = editingFee ? `/api/admin/fees/${editingFee.id}` : '/api/admin/fees';
      const method = editingFee ? 'PUT' : 'POST';
      return apiRequest(method, endpoint, submitData);
    },
    onSuccess: () => {
      toast({
        title: editingFee ? 'Fee Updated' : 'Fee Created',
        description: `Fee structure has been successfully ${editingFee ? 'updated' : 'created'}.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/fees'] });
      form.reset();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || `Failed to ${editingFee ? 'update' : 'create'} fee structure.`,
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: FeeFormData) => {
    mutation.mutate(data);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>{editingFee ? 'Edit Fee Structure' : 'Add New Fee Structure'}</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="classId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Class</FormLabel>
                    <FormControl>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select class" />
                        </SelectTrigger>
                        <SelectContent>
                          {(classes as any[]).map((cls: any) => (
                            <SelectItem key={cls.id} value={cls.id}>
                              {cls.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="courseType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Course Type</FormLabel>
                    <FormControl>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select course type" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="monthly">Monthly</SelectItem>
                          <SelectItem value="yearly">Yearly</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="admissionFee"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Admission Fee (â‚¹)</FormLabel>
                  <FormControl>
                    <Input 
                      type="number" 
                      placeholder="5000"
                      min="0"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="monthlyFee"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Monthly Fee (â‚¹)</FormLabel>
                    <FormControl>
                      <Input 
                        type="number" 
                        placeholder="2000"
                        min="0"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="yearlyFee"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Yearly Fee (â‚¹)</FormLabel>
                    <FormControl>
                      <Input 
                        type="number" 
                        placeholder="20000"
                        min="0"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description (Optional)</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Additional details about this fee structure"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="bg-blue-50 p-4 rounded-lg">
              <p className="text-sm text-blue-800">
                <strong>Note:</strong> When a student is added to an SO center and selects this class, 
                these fees will be automatically applicable. The admission fee is charged once during enrollment.
              </p>
            </div>

            <div className="flex justify-end space-x-3 pt-4">
              <Button type="button" variant="outline" onClick={onClose}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={mutation.isPending}
                className="bg-primary text-white hover:bg-blue-700"
              >
                {mutation.isPending 
                  ? (editingFee ? 'Updating...' : 'Creating...') 
                  : (editingFee ? 'Update Fee' : 'Create Fee')
                }
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

export default function Fees() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [editingFee, setEditingFee] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch real fees data from API
  const { data: fees = [], isLoading } = useQuery({
    queryKey: ['/api/admin/fees'],
  });

  // Fetch classes for displaying class names
  const { data: classes = [] } = useQuery({
    queryKey: ['/api/classes'],
  });

  const deleteFeeMutation = useMutation({
    mutationFn: async (feeId: string) => {
      return apiRequest('DELETE', `/api/admin/fees/${feeId}`);
    },
    onSuccess: () => {
      toast({
        title: 'Fee Deleted',
        description: 'Fee structure has been successfully deleted.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/fees'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete fee structure.',
        variant: 'destructive',
      });
    },
  });

  const handleEdit = (fee: any) => {
    setEditingFee(fee);
    setIsAddModalOpen(true);
  };

  const handleDelete = (feeId: string) => {
    if (confirm('Are you sure you want to delete this fee structure?')) {
      deleteFeeMutation.mutate(feeId);
    }
  };

  const handleCloseModal = () => {
    setIsAddModalOpen(false);
    setEditingFee(null);
  };

  const getClassName = (classId: string) => {
    const cls = (classes as any[]).find((c: any) => c.id === classId);
    return cls?.name || 'Unknown';
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-64">Loading fee structures...</div>;
  }

  return (
    <DashboardLayout 
      title="Fee Management" 
      subtitle="Manage class-wise fee structures"
      showAddButton={true}
      onAddClick={() => setIsAddModalOpen(true)}
    >
      <div className="space-y-6">

      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">Fee Structures</h2>
          <p className="text-sm text-gray-600">Class-wise monthly, yearly, and admission fees</p>
        </div>

        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Class</TableHead>
              <TableHead>Admission Fee</TableHead>
              <TableHead>Monthly Fee</TableHead>
              <TableHead>Yearly Fee</TableHead>
              <TableHead>Status</TableHead>
              <TableHead>Students</TableHead>
              <TableHead>Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {(fees as any[]).map((fee: any) => (
              <TableRow key={fee.id}>
                <TableCell className="font-medium">{getClassName(fee.classId)}</TableCell>
                <TableCell>
                  <div className="flex items-center space-x-1">
                    <IndianRupee className="w-4 h-4 text-green-600" />
                    <span className="font-medium text-green-600">
                      {formatCurrency(fee.admissionFee).replace('â‚¹', '')}
                    </span>
                  </div>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-1">
                    <Calendar className="w-4 h-4 text-blue-600" />
                    <span>{formatCurrency(fee.monthlyFee).replace('â‚¹', '')}</span>
                  </div>
                </TableCell>
                <TableCell>
                  <div className="flex items-center space-x-1">
                    <Calendar className="w-4 h-4 text-purple-600" />
                    <span>{formatCurrency(fee.yearlyFee).replace('â‚¹', '')}</span>
                  </div>
                </TableCell>
                <TableCell>
                  <Badge variant={fee.isActive ? "default" : "secondary"}>
                    {fee.isActive ? 'Active' : 'Inactive'}
                  </Badge>
                </TableCell>
                <TableCell>
                  <span className="text-sm text-gray-600">{fee.studentsEnrolled} enrolled</span>
                </TableCell>
                <TableCell>
                  <div className="flex space-x-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(fee)}
                    >
                      <Edit className="w-4 h-4 mr-1" />
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleDelete(fee.id)}
                      className="text-red-600 hover:text-red-700 hover:border-red-300"
                    >
                      <Trash2 className="w-4 h-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-gradient-to-br from-green-50 to-green-100 rounded-lg p-6 border border-green-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-600 text-sm font-medium">Total Monthly Revenue</p>
              <p className="text-2xl font-bold text-green-800">
                {formatCurrency((fees as any[]).reduce((sum, fee) => sum + ((fee.monthlyFee || 0) * (fee.studentsEnrolled || 0)), 0))}
              </p>
            </div>
            <IndianRupee className="w-8 h-8 text-green-600" />
          </div>
        </div>

        <div className="bg-gradient-to-br from-blue-50 to-blue-100 rounded-lg p-6 border border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-600 text-sm font-medium">Active Fee Structures</p>
              <p className="text-2xl font-bold text-blue-800">
                {(fees as any[]).filter(fee => fee.isActive).length}
              </p>
            </div>
            <Calendar className="w-8 h-8 text-blue-600" />
          </div>
        </div>

        <div className="bg-gradient-to-br from-purple-50 to-purple-100 rounded-lg p-6 border border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-600 text-sm font-medium">Total Students</p>
              <p className="text-2xl font-bold text-purple-800">
                {(fees as any[]).reduce((sum, fee) => sum + (fee.studentsEnrolled || 0), 0)}
              </p>
            </div>
            <Calendar className="w-8 h-8 text-purple-600" />
          </div>
        </div>
      </div>

        <AddEditFeeModal 
          isOpen={isAddModalOpen}
          onClose={handleCloseModal}
          editingFee={editingFee}
        />
      </div>
    </DashboardLayout>
  );
}

