import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMutation, useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { insertTeacherSchema, type InsertTeacher } from '@shared/schema';
import { z } from 'zod';

const teacherFormSchema = insertTeacherSchema.extend({
  subjectIds: z.array(z.string()).min(1, 'At least one subject must be selected'),
  classIds: z.array(z.string()).min(1, 'At least one class must be assigned'),
});

type TeacherFormData = z.infer<typeof teacherFormSchema>;

interface CreateTeacherFormProps {
  onSuccess: () => void;
}

export function CreateTeacherForm({ onSuccess }: CreateTeacherFormProps) {
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>([]);
  const [selectedClasses, setSelectedClasses] = useState<string[]>([]);
  const { toast } = useToast();

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch,
  } = useForm<TeacherFormData>({
    resolver: zodResolver(teacherFormSchema),
    defaultValues: {
      salaryType: 'fixed',
      subjectIds: [],
      classIds: [],
    },
  });

  // Fetch required data
  const { data: subjects = [] } = useQuery({
    queryKey: ['/api/subjects'],
  });

  const { data: classes = [] } = useQuery({
    queryKey: ['/api/classes'],
  });

  const { data: villages = [] } = useQuery({
    queryKey: ['/api/admin/addresses/villages'],
  });

  // Create teacher mutation
  const createTeacherMutation = useMutation({
    mutationFn: async (data: TeacherFormData) => {
      return apiRequest('POST', '/api/admin/teachers', data);
    },
    onSuccess: () => {
      toast({
        title: 'Teacher Created',
        description: 'Teacher has been successfully created with subject and class assignments.',
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to create teacher.',
        variant: 'destructive',
      });
    },
  });

  const handleSubjectChange = (subjectId: string, checked: boolean) => {
    const newSubjects = checked
      ? [...selectedSubjects, subjectId]
      : selectedSubjects.filter(id => id !== subjectId);

    setSelectedSubjects(newSubjects);
    setValue('subjectIds', newSubjects);
  };

  const handleClassChange = (classId: string, checked: boolean) => {
    const newClasses = checked
      ? [...selectedClasses, classId]
      : selectedClasses.filter(id => id !== classId);

    setSelectedClasses(newClasses);
    setValue('classIds', newClasses);
  };

  const onSubmit = (data: TeacherFormData) => {
    createTeacherMutation.mutate(data);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Personal Information */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Personal Information</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="name">Full Name *</Label>
              <Input
                id="name"
                {...register('name')}
                placeholder="Enter teacher's full name"
              />
              {errors.name && <p className="text-sm text-red-600">{errors.name.message}</p>}
            </div>

            <div>
              <Label htmlFor="fatherName">Father's Name *</Label>
              <Input
                id="fatherName"
                {...register('fatherName')}
                placeholder="Enter father's name"
              />
              {errors.fatherName && <p className="text-sm text-red-600">{errors.fatherName.message}</p>}
            </div>

            <div>
              <Label htmlFor="dateOfBirth">Date of Birth *</Label>
              <Input
                id="dateOfBirth"
                type="date"
                {...register('dateOfBirth')}
              />
              {errors.dateOfBirth && <p className="text-sm text-red-600">{errors.dateOfBirth.message}</p>}
            </div>

            <div>
              <Label htmlFor="mobile">Mobile Number *</Label>
              <Input
                id="mobile"
                {...register('mobile')}
                placeholder="Enter 10-digit mobile number"
              />
              {errors.mobile && <p className="text-sm text-red-600">{errors.mobile.message}</p>}
            </div>
          </CardContent>
        </Card>

        {/* Address & Salary */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Address & Salary</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="address">Residential Address *</Label>
              <Textarea
                id="address"
                {...register('address')}
                placeholder="Enter complete residential address"
                rows={3}
              />
              {errors.address && <p className="text-sm text-red-600">{errors.address.message}</p>}
            </div>

            <div>
              <Label htmlFor="villageId">Village</Label>
              <Select onValueChange={(value) => setValue('villageId', value)}>
                <SelectTrigger>
                  <SelectValue placeholder="Select village" />
                </SelectTrigger>
                <SelectContent>
                  {villages.map((village: any) => (
                    <SelectItem key={village.id} value={village.id}>
                      {village.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="salary">Salary Amount *</Label>
                <Input
                  id="salary"
                  type="number"
                  step="0.01"
                  {...register('salary')}
                  placeholder="Enter amount"
                />
                {errors.salary && <p className="text-sm text-red-600">{errors.salary.message}</p>}
              </div>

              <div>
                <Label htmlFor="salaryType">Salary Type *</Label>
                <Select onValueChange={(value) => setValue('salaryType', value as 'fixed' | 'hourly')}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="fixed">Fixed Monthly</SelectItem>
                    <SelectItem value="hourly">Hourly Rate</SelectItem>
                  </SelectContent>
                </Select>
                {errors.salaryType && <p className="text-sm text-red-600">{errors.salaryType.message}</p>}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Subject Assignments */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Subject Assignments *</CardTitle>
            <p className="text-sm text-gray-600">Select subjects this teacher will teach</p>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 max-h-40 overflow-y-auto">
              {subjects.map((subject: any) => (
                <div key={subject.id} className="flex items-center space-x-2">
                  <Checkbox
                    id={`subject-${subject.id}`}
                    checked={selectedSubjects.includes(subject.id)}
                    onCheckedChange={(checked) => handleSubjectChange(subject.id, checked as boolean)}
                  />
                  <Label htmlFor={`subject-${subject.id}`} className="text-sm">
                    {subject.name}
                  </Label>
                </div>
              ))}
            </div>
            {errors.subjectIds && <p className="text-sm text-red-600 mt-2">{errors.subjectIds.message}</p>}
          </CardContent>
        </Card>

        {/* Class Assignments */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Class Assignments *</CardTitle>
            <p className="text-sm text-gray-600">Assign classes this teacher will handle</p>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 max-h-40 overflow-y-auto">
              {classes.map((classItem: any) => (
                <div key={classItem.id} className="flex items-center space-x-2">
                  <Checkbox
                    id={`class-${classItem.id}`}
                    checked={selectedClasses.includes(classItem.id)}
                    onCheckedChange={(checked) => handleClassChange(classItem.id, checked as boolean)}
                  />
                  <Label htmlFor={`class-${classItem.id}`} className="text-sm">
                    {classItem.name}
                  </Label>
                </div>
              ))}
            </div>
            {errors.classIds && <p className="text-sm text-red-600 mt-2">{errors.classIds.message}</p>}
          </CardContent>
        </Card>
      </div>

      {/* Submit Button */}
      <div className="flex justify-end gap-4">
        <Button 
          type="submit" 
          disabled={createTeacherMutation.isPending}
          className="min-w-32"
        >
          {createTeacherMutation.isPending ? 'Creating...' : 'Create Teacher'}
        </Button>
      </div>
    </form>
  );
}

server/storage/teacherStorage.ts.content:
import { eq, and, sql } from 'drizzle-orm';
import { db } from '../storage';
import {
  teachers,
  teacherSubjects,
  teacherClasses,
  teacherDailyRecords,
  subjects,
  classes,
  chapters,
  topics,
  type Teacher,
  type TeacherSubject,
  type TeacherClass,
  type TeacherDailyRecord,
  type InsertTeacher,
  type InsertTeacherDailyRecord
} from '@shared/schema';

export class TeacherStorage {
  // Get all teachers with their assignments
  async getAllTeachers(): Promise<any[]> {
    const result = await db
      .select({
        id: teachers.id,
        name: teachers.name,
        fatherName: teachers.fatherName,
        mobile: teachers.mobile,
        address: teachers.address,
        salary: teachers.salary,
        salaryType: teachers.salaryType,
        dateOfBirth: teachers.dateOfBirth,
        villageId: teachers.villageId,
        isActive: teachers.isActive,
        createdAt: teachers.createdAt,
        updatedAt: teachers.updatedAt,
      })
      .from(teachers)
      .where(eq(teachers.isActive, true))
      .orderBy(teachers.createdAt);

    return result;
  }

  // Get teacher by ID
  async getTeacherById(id: string): Promise<Teacher | undefined> {
    const [teacher] = await db
      .select()
      .from(teachers)
      .where(eq(teachers.id, id));

    return teacher;
  }

  // Create teacher with subject and class assignments
  async createTeacher(data: InsertTeacher & { subjectIds: string[], classIds: string[] }): Promise<Teacher> {
    const { subjectIds, classIds, ...teacherData } = data;

    return await db.transaction(async (tx) => {
      // Create teacher
      const [teacher] = await tx
        .insert(teachers)
        .values(teacherData)
        .returning();

      // Assign subjects
      if (subjectIds.length > 0) {
        await tx
          .insert(teacherSubjects)
          .values(subjectIds.map(subjectId => ({
            teacherId: teacher.id,
            subjectId
          })));
      }

      // Assign classes
      if (classIds.length > 0) {
        await tx
          .insert(teacherClasses)
          .values(classIds.map(classId => ({
            teacherId: teacher.id,
            classId
          })));
      }

      return teacher;
    });
  }

  // Get teacher's assigned subjects
  async getTeacherSubjects(teacherId: string): Promise<any[]> {
    const result = await db
      .select({
        id: subjects.id,
        name: subjects.name,
      })
      .from(teacherSubjects)
      .innerJoin(subjects, eq(teacherSubjects.subjectId, subjects.id))
      .where(eq(teacherSubjects.teacherId, teacherId));

    return result;
  }

  // Get teacher's assigned classes
  async getTeacherClasses(teacherId: string): Promise<any[]> {
    const result = await db
      .select({
        id: classes.id,
        name: classes.name,
      })
      .from(teacherClasses)
      .innerJoin(classes, eq(teacherClasses.classId, classes.id))
      .where(eq(teacherClasses.teacherId, teacherId));

    return result;
  }

  // Get teacher's daily records
  async getTeacherRecords(teacherId: string): Promise<any[]> {
    const result = await db
      .select({
        id: teacherDailyRecords.id,
        teacherId: teacherDailyRecords.teacherId,
        recordDate: teacherDailyRecords.recordDate,
        classId: teacherDailyRecords.classId,
        subjectId: teacherDailyRecords.subjectId,
        chapterId: teacherDailyRecords.chapterId,
        topicId: teacherDailyRecords.topicId,
        teachingDuration: teacherDailyRecords.teachingDuration,
        notes: teacherDailyRecords.notes,
        createdAt: teacherDailyRecords.createdAt,
        className: classes.name,
        subjectName: subjects.name,
        chapterName: chapters.name,
        topicName: topics.name,
      })
      .from(teacherDailyRecords)
      .leftJoin(classes, eq(teacherDailyRecords.classId, classes.id))
      .leftJoin(subjects, eq(teacherDailyRecords.subjectId, subjects.id))
      .leftJoin(chapters, eq(teacherDailyRecords.chapterId, chapters.id))
      .leftJoin(topics, eq(teacherDailyRecords.topicId, topics.id))
      .where(eq(teacherDailyRecords.teacherId, teacherId))
      .orderBy(sql`${teacherDailyRecords.recordDate} DESC`);

    return result;
  }

  // Add daily teaching record
  async addTeachingRecord(data: InsertTeacherDailyRecord): Promise<TeacherDailyRecord> {
    const [record] = await db
      .insert(teacherDailyRecords)
      .values({
        ...data,
        // Convert date string to proper format if needed
        recordDate: typeof data.recordDate === 'string' ? data.recordDate : data.recordDate,
      })
      .returning();

    return record;
  }

  // Update teacher
  async updateTeacher(id: string, data: Partial<InsertTeacher>): Promise<Teacher> {
    const [teacher] = await db
      .update(teachers)
      .set({
        ...data,
        updatedAt: new Date(),
      })
      .where(eq(teachers.id, id))
      .returning();

    return teacher;
  }

  // Delete teacher (soft delete)
  async deleteTeacher(id: string): Promise<void> {
    await db
      .update(teachers)
      .set({
        isActive: false,
        updatedAt: new Date(),
      })
      .where(eq(teachers.id, id));
  }

  // Update teacher subject assignments
  async updateTeacherSubjects(teacherId: string, subjectIds: string[]): Promise<void> {
    await db.transaction(async (tx) => {
      // Remove existing assignments
      await tx
        .delete(teacherSubjects)
        .where(eq(teacherSubjects.teacherId, teacherId));

      // Add new assignments
      if (subjectIds.length > 0) {
        await tx
          .insert(teacherSubjects)
          .values(subjectIds.map(subjectId => ({
            teacherId,
            subjectId
          })));
      }
    });
  }

  // Update teacher class assignments
  async updateTeacherClasses(teacherId: string, classIds: string[]): Promise<void> {
    await db.transaction(async (tx) => {
      // Remove existing assignments
      await tx
        .delete(teacherClasses)
        .where(eq(teacherClasses.teacherId, teacherId));

      // Add new assignments
      if (classIds.length > 0) {
        await tx
          .insert(teacherClasses)
          .values(classIds.map(classId => ({
            teacherId,
            classId
          })));
      }
    });
  }
}

