                        <div className="h-48 w-full">
                          <ResponsiveContainer width="100%" height="100%">
                            <PieChart>
                              <Pie
                                data={attendancePieData}
                                cx="50%"
                                cy="50%"
                                outerRadius={60}
                                dataKey="value"
                                label={({ name, value, percent }) => `${name}: ${value} (${(percent * 100).toFixed(1)}%)`}
                              >
                                {attendancePieData.map((entry, index) => (
                                  <Cell key={`cell-${index}`} fill={entry.color} />
                                ))}
                              </Pie>
                              <Tooltip />
                            </PieChart>
                          </ResponsiveContainer>
                        </div>

                        <div className="grid grid-cols-3 gap-4 text-center text-sm">
                          <div>
                            <p className="font-medium text-gray-600">Total Days</p>
                            <p className="text-lg font-bold text-blue-600">{(attendanceData as any).totalDays || 0}</p>
                          </div>
                          <div>
                            <p className="font-medium text-gray-600">Present</p>
                            <p className="text-lg font-bold text-green-600">{(attendanceData as any).presentDays || 0}</p>
                          </div>
                          <div>
                            <p className="font-medium text-gray-600">Absent</p>
                            <p className="text-lg font-bold text-red-600">{(attendanceData as any).absentDays || 0}</p>
                          </div>
                        </div>
                      </div>
                    ) : (
                      <p className="text-gray-500 text-center py-4">No attendance data available</p>
                    )}
                  </CardContent>
                </Card>
              </div>
            </TabsContent>

            {/* Progress Tab */}
            <TabsContent value="progress" className="space-y-6">
              {(progressData as any)?.subjectData?.length > 0 ? (
                <div className="space-y-6">
                  {/* Subject-wise Performance Bar Chart */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2">
                        <BarChart3 className="h-5 w-5" />
                        Subject-wise Performance
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="h-80 w-full">
                        <ResponsiveContainer width="100%" height="100%">
                          <BarChart data={(progressData as any).subjectData} margin={{ top: 20, right: 30, left: 20, bottom: 60 }}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis 
                              dataKey="subject" 
                              angle={-45}
                              textAnchor="end"
                              height={80}
                              interval={0}
                            />
                            <YAxis domain={[0, 100]} />
                            <Tooltip />
                            <Bar dataKey="homeworkPercentage" fill="#0088FE" name="Homework %" />
                            <Bar dataKey="tuitionPercentage" fill="#00C49F" name="Tuition %" />
                            <Bar dataKey="averageScore" fill="#FFBB28" name="Average Score %" />
                          </BarChart>
