                    <CardTitle className="flex items-center gap-2">
                      <FileText className="h-5 w-5" />
                      Recent Homework
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {(progressData as any)?.recentHomework?.length > 0 ? (
                      <div className="space-y-3">
