import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useLocation } from 'wouter';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import DashboardLayout from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import IndividualStudentMarksModal from '@/components/exam/IndividualStudentMarksModal';
import { 
  GraduationCap, 
  Clock, 
  CheckCircle, 
  AlertCircle,
  Users,
  Plus,
  Minus,
  Edit3,
  FileEdit,
  Loader2
} from 'lucide-react';

interface ExamResultStudent {
  id: string;
  name: string;
  regId: string;
  marksObtained: number;
  answeredQuestions: 'not_answered' | 'partially_answered' | 'fully_answered';
}

export default function SoCenterExamManagement() {
  const [selectedExamId, setSelectedExamId] = useState('');
  const [isResultsModalOpen, setIsResultsModalOpen] = useState(false);
  const [examResults, setExamResults] = useState<ExamResultStudent[]>([]);
  const [activeTab, setActiveTab] = useState('progress-tracking');

  // Individual student marks modal state
  const [isIndividualMarksModalOpen, setIsIndividualMarksModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState<any>(null);
  const [selectedExam, setSelectedExam] = useState<any>(null);

  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();
  const [, setLocation] = useLocation();

  // Get SO Center ID from logged-in user (assuming user has soCenterId or similar field)
  const soCenterId = user?.id; // Using user ID for now, will be mapped server-side

  // Mark exam as completed mutation - moved to top
  const markCompletedMutation = useMutation({
    mutationFn: async (examId: string) => {
      const response = await apiRequest('POST', `/api/so-center/exams/${examId}/complete`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Exam Marked Complete",
        description: "The exam has been marked as completed successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/so-center/exams'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: `Failed to mark exam as completed. ${error.message || ''}`,
        variant: "destructive",
      });
    },
  });

  // Submit results mutation - moved to top
  const submitResultsMutation = useMutation({
    mutationFn: async ({ examId, results }: { examId: string; results: ExamResultStudent[] }) => {
      const response = await apiRequest('POST', `/api/so-center/exams/${examId}/results`, {
        results: results,
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Results Submitted",
        description: "Exam results have been posted successfully.",
      });
      setIsResultsModalOpen(false);
      setExamResults([]);
      queryClient.invalidateQueries({ queryKey: ['/api/so-center/exams'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: `Failed to submit results. ${error.message || ''}`,
        variant: "destructive",
      });
    },
  });

  // Get SO Center exams with optimized caching
  const { data: exams = [], isLoading: isLoadingExams, error: examsError } = useQuery<any[]>({
    queryKey: ['/api/so-center/exams'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/so-center/exams');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const examsData = await response.json();
      console.log('âœ… SO Center exams fetched successfully:', examsData.length);
      return Array.isArray(examsData) ? examsData : [];
    },
    retry: 2,
    staleTime: 15 * 60 * 1000, // 15 minutes cache
    refetchOnWindowFocus: false,
    refetchOnReconnect: false,
    refetchInterval: false,
  });

  // Get SO Center students with optimized caching
  const { data: students = [], isLoading: isLoadingStudents, error: studentsError } = useQuery<any[]>({
    queryKey: ['/api/so-center/students'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/so-center/students');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const studentsData = await response.json();
      console.log('âœ… SO Center students fetched successfully:', studentsData.length);
      return Array.isArray(studentsData) ? studentsData : [];
    },
    retry: 2,
    staleTime: 10 * 60 * 1000, // 10 minutes cache
    refetchOnWindowFocus: false,
    refetchOnReconnect: false,
    refetchInterval: false,
  });

  // Simplified loading state
  if (isLoadingExams || isLoadingStudents) {
    return (
      <DashboardLayout title="Exam Management" subtitle="Manage exams and post results for your center">
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" />
            <p className="text-gray-600">Loading exams and students...</p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  // Handle error states
  if (examsError) {
    return (
      <DashboardLayout title="Exam Management" subtitle="Manage exams and post results for your center">
        <div className="flex flex-col items-center justify-center min-h-[400px] text-red-500">
          <AlertCircle className="h-12 w-12 mb-4" />
          <p className="text-lg font-semibold">Failed to load exams</p>
          <p className="text-gray-600">Error: {examsError.message}</p>
          <Button onClick={() => queryClient.invalidateQueries({ queryKey: ['/api/so-center/exams'] })} className="mt-4">
            Retry
          </Button>
        </div>
      </DashboardLayout>
    );
  }

  if (studentsError) {
    return (
      <DashboardLayout title="Exam Management" subtitle="Manage exams and post results for your center">
        <div className="flex flex-col items-center justify-center min-h-[400px] text-red-500">
          <AlertCircle className="h-12 w-12 mb-4" />
          <p className="text-lg font-semibold">Failed to load students</p>
          <p className="text-gray-600">Error: {studentsError.message}</p>
          <Button onClick={() => queryClient.invalidateQueries({ queryKey: ['/api/so-center/students'] })} className="mt-4">
            Retry
          </Button>
        </div>
      </DashboardLayout>
    );
  }




  const openResultsModal = (exam: any) => {
    setSelectedExamId(exam.id);
    setSelectedExam(exam);
    // Initialize results with class students
    const classStudents = students.filter((student: any) => student.classId === exam.classId);
    const initialResults = classStudents.map((student: any) => ({
      id: student.id,
      name: student.name,
      regId: student.regId,
      marksObtained: 0,
      answeredQuestions: 'not_answered' as const,
    }));
    setExamResults(initialResults);
    setIsResultsModalOpen(true);
  };

  const openIndividualMarksModal = (student: any, exam: any) => {
    setSelectedStudent(student);
    setSelectedExam(exam);
    setIsIndividualMarksModalOpen(true);
  };

  const closeIndividualMarksModal = () => {
    setIsIndividualMarksModalOpen(false);
    setSelectedStudent(null);
    setSelectedExam(null);
  };

  const updateStudentResult = (studentId: string, field: string, value: any) => {
    setExamResults(prev => prev.map(result => 
      result.id === studentId 
        ? { ...result, [field]: value }
        : result
    ));
  };

  const addStudentToCategory = (category: 'not_answered' | 'partially_answered' | 'fully_answered') => {
    const availableStudents = students.filter((student: any) => 
      !examResults.some(result => result.id === student.id)
    );

    if (availableStudents.length > 0) {
      const newStudent = availableStudents[0];
      setExamResults(prev => [...prev, {
        id: newStudent.id,
        name: newStudent.name,
        regId: newStudent.regId,
        marksObtained: 0,
        answeredQuestions: category,
      }]);
    }
  };

  const removeStudentFromResults = (studentId: string) => {
    setExamResults(prev => prev.filter(result => result.id !== studentId));
  };

  const getTimeRemaining = (examDate: string) => {
    const now = new Date();
    const exam = new Date(examDate);
    const diff = exam.getTime() - now.getTime();

    if (diff <= 0) {
      return 'Exam date passed';
    }

    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

    if (days > 0) {
      return `${days} days, ${hours} hours remaining`;
    } else {
      return `${hours} hours remaining`;
    }
  };

  const getExamStatus = (exam: any) => {
    const now = new Date();
    const examDate = new Date(exam.examDate);

    if (exam.status === 'completed') {
      return { status: 'completed', color: 'bg-green-500', text: 'Completed' };
    }

    if (examDate < now) {
      return { status: 'overdue', color: 'bg-red-500', text: 'Overdue' };
    }

    return { status: 'scheduled', color: 'bg-blue-500', text: 'Scheduled' };
  };

  const categorizedResults = {
    not_answered: examResults.filter(r => r.answeredQuestions === 'not_answered'),
    partially_answered: examResults.filter(r => r.answeredQuestions === 'partially_answered'),
    fully_answered: examResults.filter(r => r.answeredQuestions === 'fully_answered'),
  };

  return (
    <DashboardLayout title="Exam Management">
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-green-800">Exam Management</h1>
            <p className="text-green-600 mt-1">Manage exams and post results for your center</p>
          </div>
          <div className="flex items-center gap-2">
            <Button
              onClick={() => {
                queryClient.invalidateQueries({ queryKey: ['/api/so-center/exams'] });
                queryClient.invalidateQueries({ queryKey: ['/api/so-center/students'] });
              }}
              variant="outline"
              size="sm"
              disabled={isLoadingExams || isLoadingStudents}
            >
              {isLoadingExams || isLoadingStudents ? (
                <Loader2 className="h-4 w-4 animate-spin mr-2" />
              ) : (
                <GraduationCap className="h-4 w-4 mr-2" />
              )}
              Refresh
            </Button>
          </div>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="progress-tracking">Progress Tracking</TabsTrigger>
            <TabsTrigger value="exams">Exams & Results</TabsTrigger>
          </TabsList>

          <TabsContent value="progress-tracking" className="mt-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <GraduationCap size={20} />
                  <span>Student Progress Overview</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-600 mb-4">Track student progress and exam performance</p>
                {/* Existing progress tracking content can be integrated here */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <Card className="bg-blue-50">
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="text-sm text-blue-600">Total Students</p>
                          <p className="text-2xl font-bold text-blue-800">{students.length}</p>
                        </div>
                        <Users className="text-blue-500" size={24} />
                      </div>
                    </CardContent>
                  </Card>

                  <Card className="bg-green-50">
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="text-sm text-green-600">Completed Exams</p>
                          <p className="text-2xl font-bold text-green-800">
                            {exams.filter(exam => exam.status === 'completed').length}
                          </p>
                        </div>
                        <CheckCircle className="text-green-500" size={24} />
                      </div>
                    </CardContent>
                  </Card>

                  <Card className="bg-orange-50">
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="text-sm text-orange-600">Pending Exams</p>
                          <p className="text-2xl font-bold text-orange-800">
                            {exams.filter(exam => exam.status !== 'completed').length}
                          </p>
                        </div>
                        <AlertCircle className="text-orange-500" size={24} />
                      </div>
                    </CardContent>
                  </Card>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="exams" className="mt-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <GraduationCap size={20} />
                  <span>Exams for Your Center</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {exams.length === 0 ? (
                  <div className="text-center py-12">
                    <GraduationCap size={64} className="mx-auto text-gray-300 mb-6" />
                    <h3 className="text-xl font-semibold text-gray-700 mb-2">No Exams Available</h3>
                    <p className="text-gray-500 mb-4">No exams have been assigned to your SO Center yet.</p>
                    <div className="bg-blue-50 p-4 rounded-lg max-w-md mx-auto">
                      <p className="text-sm text-blue-700">
                        <strong>Note:</strong> Exams are created and assigned by the admin. 
                        Contact your administrator if you expect to see exams here.
                      </p>
                    </div>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {exams.map((exam: any) => {
                      const status = getExamStatus(exam);
                      return (
                        <Card key={exam.id} className="p-4">
                          <div className="flex items-center justify-between">
                            <div className="flex-1">
                              <div className="flex items-center gap-3 mb-2">
                                <h3 className="text-lg font-semibold">{exam.title}</h3>
                                <Badge className={`${status.color} text-white text-xs`}>
                                  {status.text}
                                </Badge>
                              </div>

                              <div className="grid grid-cols-2 lg:grid-cols-4 gap-3 text-sm">
                                <div>
                                  <span className="text-gray-500">Class:</span>
                                  <span className="ml-1 font-medium">{exam.className}</span>
                                </div>
                                <div>
                                  <span className="text-gray-500">Subject:</span>
                                  <span className="ml-1 font-medium">{exam.subjectName}</span>
                                </div>
                                <div>
                                  <span className="text-gray-500">Date:</span>
                                  <span className="ml-1 font-medium">{new Date(exam.examDate).toLocaleDateString('en-GB')}</span>
                                </div>
                                <div>
                                  <span className="text-gray-500">Marks:</span>
                                  <span className="ml-1 font-medium">{exam.totalMarks}</span>
                                </div>
                              </div>
                            </div>

                            <div className="flex gap-2 ml-4">
                              {exam.status !== 'completed' ? (
                                <>
                                  <Button
                                    size="sm"
                                    onClick={() => markCompletedMutation.mutate(exam.id)}
                                    disabled={markCompletedMutation.isPending}
                                    className="bg-blue-600 hover:bg-blue-700"
                                  >
                                    <CheckCircle size={14} className="mr-1" />
                                    Complete
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="outline"
                                    onClick={() => setLocation(`/post-exam-result/${exam.id}`)}
                                  >
                                    <FileEdit size={14} className="mr-1" />
                                    Results
                                  </Button>
                                </>
                              ) : (
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => setLocation(`/post-exam-result/${exam.id}`)}
                                >
                                  <Edit3 size={14} className="mr-1" />
                                  Update
                                </Button>
                              )}
                            </div>
                          </div>
                        </Card>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>



        {/* Individual Student Marks Modal */}
        <IndividualStudentMarksModal
          isOpen={isIndividualMarksModalOpen}
          onClose={closeIndividualMarksModal}
          student={selectedStudent}
          exam={selectedExam}
        />
      </div>
    </DashboardLayout>
  );
}

client/src/components/ExamCreationForm.t.content:
import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { MapPin } from "lucide-react";

interface ExamCreationFormProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (formData: FormData) => void;
  classes: any[];
  subjects: any[];
  filteredSoCenters: any[];
  selectedSoCenterIds: string[];
  setSelectedSoCenterIds: (ids: string[]) => void;
}

export function ExamCreationForm({
  isOpen,
  onClose,
  onSubmit,
  classes,
  subjects,
  filteredSoCenters,
  selectedSoCenterIds,
  setSelectedSoCenterIds
}: ExamCreationFormProps) {
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget as HTMLFormElement);
    onSubmit(formData);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Create New Exam</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Basic Information */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="name">Exam Name</Label>
              <Input id="name" name="name" placeholder="Enter exam name" required />
            </div>
            <div>
              <Label htmlFor="examDate">Exam Date</Label>
              <Input id="examDate" name="examDate" type="date" required />
            </div>
          </div>

          <div>
            <Label htmlFor="description">Description</Label>
            <Textarea id="description" name="description" placeholder="Enter exam description" />
          </div>

          {/* Academic Structure */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="classId">Class</Label>
              <Select name="classId" required>
                <SelectTrigger>
                  <SelectValue placeholder="Select class" />
                </SelectTrigger>
                <SelectContent>
                  {classes.filter((cls: any) => cls.id && cls.id.trim() !== '').map((cls: any) => (
                    <SelectItem key={cls.id} value={cls.id}>
                      {cls.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="subjectId">Subject</Label>
              <Select name="subjectId" required>
                <SelectTrigger>
                  <SelectValue placeholder="Select subject" />
                </SelectTrigger>
                <SelectContent>
                  {subjects.filter((subject: any) => subject.id && subject.id.trim() !== '').map((subject: any) => (
                    <SelectItem key={subject.id} value={subject.id}>
                      {subject.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Exam Configuration */}
          <div className="grid grid-cols-3 gap-4">
            <div>
              <Label htmlFor="duration">Duration (minutes)</Label>
              <Input id="duration" name="duration" type="number" placeholder="120" required />
            </div>
            <div>
              <Label htmlFor="totalQuestions">Total Questions</Label>
              <Input id="totalQuestions" name="totalQuestions" type="number" placeholder="50" required />
            </div>
            <div>
              <Label htmlFor="maxMarks">Maximum Marks</Label>
              <Input id="maxMarks" name="maxMarks" type="number" placeholder="100" required />
            </div>
          </div>

          <div>
            <Label htmlFor="passingMarks">Passing Marks</Label>
            <Input id="passingMarks" name="passingMarks" type="number" placeholder="35" required className="w-1/3" />
          </div>

          {/* SO Centers Selection */}
          <div>
            <div className="flex items-center justify-between mb-3">
              <Label>SO Centers (Select Multiple)</Label>
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={() => {
                  if (selectedSoCenterIds.length === filteredSoCenters.length) {
                    setSelectedSoCenterIds([]);
                  } else {
                    setSelectedSoCenterIds(filteredSoCenters.map((center: any) => center.id));
                  }
                }}
                className="text-xs"
              >
                {selectedSoCenterIds.length === filteredSoCenters.length ? 'Unselect All' : 'Select All'}
              </Button>
            </div>
            <div className="grid grid-cols-2 gap-2 max-h-40 overflow-y-auto border rounded-lg p-3">
              {filteredSoCenters.length === 0 ? (
                <div className="col-span-2 text-center text-gray-500 py-4">
                  <p>No SO Centers available</p>
                  <p className="text-xs">Try adjusting the location filters</p>
                </div>
              ) : (
                filteredSoCenters.map((center: any) => (
                  <div key={center.id} className="flex items-center space-x-2">
                    <Checkbox
                      id={`center-${center.id}`}
                      checked={selectedSoCenterIds.includes(center.id)}
                      onCheckedChange={(checked) => {
                        if (checked) {
                          setSelectedSoCenterIds([...selectedSoCenterIds, center.id]);
                        } else {
                          setSelectedSoCenterIds(selectedSoCenterIds.filter(id => id !== center.id));
                        }
                      }}
                    />
                    <Label htmlFor={`center-${center.id}`} className="text-xs">
                      <div className="flex items-center space-x-1">
                        <MapPin size={12} />
                        <span>{center.name || center.centerName || 'Unnamed Center'}</span>
                        <span className="text-gray-500">({center.code || center.id.slice(0, 8)})</span>
                      </div>
                    </Label>
                  </div>
                ))
              )}
            </div>
            <p className="text-xs text-gray-500 mt-2">
              Selected: {selectedSoCenterIds.length} of {filteredSoCenters.length} centers
            </p>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-2 pt-4">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" className="bg-primary text-white">
              Create Exam
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/announcements/Anno.content:
import React, { useEffect, useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/hooks/useAuth';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { X, Megaphone, Calendar, Users, AlertCircle } from 'lucide-react';

interface Announcement {
  id: string;
  title: string;
  description: string;
  content?: string;
  targetAudience: string[];
  priority: 'low' | 'normal' | 'high' | 'urgent';
  imageUrl?: string;
  fromDate: string;
  toDate: string;
  isActive: boolean;
  showOnQrCode: boolean;
  createdAt: string;
}

export function AnnouncementsPopup() {
  const { user } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [dismissedAnnouncements, setDismissedAnnouncements] = useState<string[]>(() => {
    const stored = localStorage.getItem('dismissedAnnouncements');
    return stored ? JSON.parse(stored) : [];
  });

  // Get announcements based on user role
  const getAnnouncementsEndpoint = () => {
    if (!user?.role) return null;

    switch (user.role) {
      case 'admin':
        return '/api/admin/active-announcements';
      case 'so_center':
        return '/api/so-center/active-announcements';
      case 'teacher':
        return '/api/teacher/active-announcements';
      default:
        return `/api/announcements/${user.role}`;
    }
  };

  const { data: announcements = [], isLoading } = useQuery({
    queryKey: ['active-announcements', user?.role],
    queryFn: async () => {
      const endpoint = getAnnouncementsEndpoint();
      if (!endpoint) return [];

      const response = await apiRequest('GET', endpoint);
      return response.json();
    },
    enabled: !!user?.role,
    refetchInterval: 5 * 60 * 1000, // Refetch every 5 minutes
  });

  // Filter out dismissed announcements and show only relevant ones
  const activeAnnouncements = announcements.filter((announcement: Announcement) => 
    !dismissedAnnouncements.includes(announcement.id) &&
    announcement.isActive &&
    new Date(announcement.fromDate) <= new Date() &&
    new Date(announcement.toDate) >= new Date() &&
    (
      announcement.targetAudience.includes('all') ||
      announcement.targetAudience.includes(user?.role || '') ||
      (user?.role === 'so_center' && announcement.targetAudience.includes('so_centers'))
    )
  );

  // Auto-open popup when there are new announcements
  useEffect(() => {
    if (activeAnnouncements.length > 0 && !isOpen) {
      setIsOpen(true);
    }
  }, [activeAnnouncements.length, isOpen]);

  const handleDismiss = (announcementId: string) => {
    const newDismissed = [...dismissedAnnouncements, announcementId];
    setDismissedAnnouncements(newDismissed);
    localStorage.setItem('dismissedAnnouncements', JSON.stringify(newDismissed));

    // Close popup if no more announcements
    if (activeAnnouncements.length === 1) {
      setIsOpen(false);
    }
  };

  const handleCloseAll = () => {
    const allIds = activeAnnouncements.map((a: Announcement) => a.id);
    const newDismissed = [...dismissedAnnouncements, ...allIds];
    setDismissedAnnouncements(newDismissed);
    localStorage.setItem('dismissedAnnouncements', JSON.stringify(newDismissed));
    setIsOpen(false);
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'normal': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'urgent':
      case 'high':
        return <AlertCircle className="h-4 w-4" />;
      default:
        return <Megaphone className="h-4 w-4" />;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (isLoading || activeAnnouncements.length === 0) {
    return null;
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader className="flex flex-row items-center justify-between">
          <DialogTitle className="flex items-center gap-2">
            <Megaphone className="h-5 w-5 text-blue-600" />
            Active Announcements ({activeAnnouncements.length})
          </DialogTitle>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleCloseAll}
            className="text-gray-500 hover:text-gray-700"
          >
            <X className="h-4 w-4" />
            Close All
          </Button>
        </DialogHeader>

        <div className="space-y-4">
          {activeAnnouncements.map((announcement: Announcement, index: number) => (
            <Card key={announcement.id} className="relative">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex items-center gap-2 flex-1">
                    <div className={`p-1 rounded ${getPriorityColor(announcement.priority)} flex items-center gap-1`}>
                      {getPriorityIcon(announcement.priority)}
                      <span className="text-xs font-medium capitalize">
                        {announcement.priority}
                      </span>
                    </div>
                    <div className="flex items-center gap-1 text-xs text-gray-500">
                      <Calendar className="h-3 w-3" />
                      {formatDate(announcement.fromDate)} - {formatDate(announcement.toDate)}
                    </div>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => handleDismiss(announcement.id)}
                    className="h-6 w-6 p-0 text-gray-400 hover:text-gray-600"
                  >
                    <X className="h-3 w-3" />
                  </Button>
                </div>
                <CardTitle className="text-lg font-semibold text-gray-900">
                  {announcement.title}
                </CardTitle>
              </CardHeader>

              <CardContent className="space-y-3">
                <p className="text-gray-700 leading-relaxed">
                  {announcement.description}
                </p>

                {announcement.content && (
                  <div className="bg-gray-50 p-3 rounded-md">
                    <p className="text-sm text-gray-600 whitespace-pre-wrap">
                      {announcement.content}
                    </p>
                  </div>
                )}

                {announcement.imageUrl && (
                  <div className="rounded-md overflow-hidden">
                    <img
                      src={announcement.imageUrl}
                      alt="Announcement banner"
                      className="w-full h-auto max-h-48 object-cover"
                      onError={(e) => {
                        e.currentTarget.style.display = 'none';
                      }}
                    />
                  </div>
                )}

                <div className="flex items-center justify-between text-xs text-gray-500">
                  <div className="flex items-center gap-1">
                    <Users className="h-3 w-3" />
                    <span>For: {announcement.targetAudience.join(', ')}</span>
                  </div>
                  <span>Posted: {formatDate(announcement.createdAt)}</span>
                </div>
              </CardContent>

              {index < activeAnnouncements.length - 1 && (
                <Separator className="my-4" />
              )}
            </Card>
          ))}
        </div>

        <div className="flex justify-end gap-2 pt-4 border-t">
          <Button variant="outline" onClick={() => setIsOpen(false)}>
            Close
          </Button>
          <Button onClick={handleCloseAll}>
            Dismiss All
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/InvoiceGenerator.t.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Receipt, Copy, Share, Download } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface CourseInvoiceData {
  transactionId: string;
  productName: string;
  studentName: string;
  coursePrice: number;
  commissionAmount: number;
  purchaseDate: string;
  agentEmail: string;
}

interface WithdrawalInvoiceData {
  transactionId: string;
  withdrawalId: string;
  amount: number;
  paymentMode: string;
  paymentDetails: string;
  userEmail: string;
  userName: string;
  processedAt: string;
  type: 'withdrawal';
}

type InvoiceData = CourseInvoiceData | WithdrawalInvoiceData;

interface InvoiceGeneratorProps {
  invoiceData: InvoiceData | null;
  isOpen: boolean;
  onClose: () => void;
}

export function InvoiceGenerator({ invoiceData, isOpen, onClose }: InvoiceGeneratorProps) {
  const { toast } = useToast();
  const [isGenerating, setIsGenerating] = useState(false);

  if (!invoiceData) return null;

  const formatCurrency = (amount: number) => {
    if (typeof amount !== 'number' || isNaN(amount)) {
      return 'â‚¹0';
    }
    return `â‚¹${amount.toLocaleString('en-IN')}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const generateInvoiceText = () => {
    if ('type' in invoiceData && invoiceData.type === 'withdrawal') {
      return `
ðŸ§¾ NAVANIDHI ACADEMY WITHDRAWAL RECEIPT
=====================================

ðŸ“‹ TRANSACTION DETAILS
Transaction ID: ${invoiceData.transactionId}
Withdrawal ID: ${invoiceData.withdrawalId}
Date & Time: ${formatDate(invoiceData.processedAt)}

ðŸ’° PAYMENT INFORMATION
Amount: ${formatCurrency(invoiceData.amount)}
Payment Mode: ${invoiceData.paymentMode.toUpperCase()}
Payment Details: ${invoiceData.paymentDetails}

ðŸ‘¤ RECIPIENT DETAILS
Name: ${invoiceData.userName}
Email: ${invoiceData.userEmail}

âœ… STATUS: APPROVED & PROCESSED
`;
    } else {
      const courseData = invoiceData as CourseInvoiceData;
      return `
ðŸ§¾ NAVANIDHI ACADEMY INVOICE
================================

ðŸ“‹ TRANSACTION DETAILS
Transaction ID: ${courseData.transactionId}
Date & Time: ${formatDate(courseData.purchaseDate)}

ðŸ‘¨â€ðŸŽ“ COURSE INFORMATION
Course Name: ${courseData.productName}
Student Name: ${courseData.studentName}
Course Fee: ${formatCurrency(courseData.coursePrice)}

ðŸ’° COMMISSION DETAILS
Agent Email: ${courseData.agentEmail}
Commission Rate: ${((courseData.commissionAmount / courseData.coursePrice) * 100).toFixed(1)}%
Commission Amount: ${formatCurrency(courseData.commissionAmount)}

ðŸ“Š PAYMENT SUMMARY
Total Course Fee: ${formatCurrency(courseData.coursePrice)}
Commission Earned: ${formatCurrency(courseData.commissionAmount)}

================================
Thank you for your business!
Navanidhi Academy Management System
`;
    }
  };

  const handleCopyInvoice = async () => {
    try {
      const invoiceText = generateInvoiceText();
      await navigator.clipboard.writeText(invoiceText);
      toast({
        title: "Invoice Copied!",
        description: "Invoice details have been copied to clipboard.",
      });
    } catch (error) {
      toast({
        title: "Copy Failed",
        description: "Failed to copy invoice to clipboard.",
        variant: "destructive",
      });
    }
  };

  const handleShareInvoice = async () => {
    try {
      const invoiceText = generateInvoiceText();
      if (navigator.share) {
        await navigator.share({
          title: 'Course Purchase Invoice',
          text: invoiceText,
        });
      } else {
        // Fallback to copy
        await navigator.clipboard.writeText(invoiceText);
        toast({
          title: "Invoice Copied!",
          description: "Invoice details copied to clipboard (sharing not supported).",
        });
      }
    } catch (error) {
      toast({
        title: "Share Failed",
        description: "Failed to share invoice.",
        variant: "destructive",
      });
    }
  };

  const handleDownloadInvoice = () => {
    setIsGenerating(true);
    try {
      const invoiceText = generateInvoiceText();
      const blob = new Blob([invoiceText], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `Invoice_${invoiceData.transactionId}.txt`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      toast({
        title: "Invoice Downloaded!",
        description: "Invoice has been saved to your device.",
      });
    } catch (error) {
      toast({
        title: "Download Failed",
        description: "Failed to download invoice.",
        variant: "destructive",
      });
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Course Purchase Invoice
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Invoice Display */}
          <div className="bg-gray-50 border border-gray-200 rounded-lg p-4 font-mono text-sm">
            <pre className="whitespace-pre-wrap text-gray-800">
              {generateInvoiceText()}
            </pre>
          </div>

          {/* Action Buttons */}
          <div className="grid grid-cols-3 gap-3">
            <Button
              variant="outline"
              onClick={handleCopyInvoice}
              className="flex items-center gap-2"
            >
              <Copy className="h-4 w-4" />
              Copy
            </Button>

            <Button
              variant="outline"
              onClick={handleShareInvoice}
              className="flex items-center gap-2"
            >
              <Share className="h-4 w-4" />
              Share
            </Button>

            <Button
              onClick={handleDownloadInvoice}
              disabled={isGenerating}
              className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700"
            >
              <Download className="h-4 w-4" />
              {isGenerating ? 'Saving...' : 'Download'}
            </Button>
          </div>

          <div className="text-center">
            <Button
              variant="ghost"
              onClick={onClose}
              className="w-full"
            >
              Close
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

