import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  Search, 
  Edit, 
  Trash2, 
  Eye,
  UserPlus,
  Download,
  Filter,
  MoreVertical,
  MapPin
} from 'lucide-react';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { AddStudentModal } from './AddStudentModal';
import { EditStudentModal } from './EditStudentModal';
// import { ViewStudentModal } from './ViewStudentModal';
import type { Student } from '@shared/schema';

export function AdminStudentsList() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState('');
  const [classFilter, setClassFilter] = useState('all');
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState<Student | null>(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [studentToDelete, setStudentToDelete] = useState<Student | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const studentsPerPage = 10;

  // Fetch students
  const { data: students = [], isLoading } = useQuery<Student[]>({
    queryKey: ['/api/students'],
  });

  // Fetch classes for filter
  const { data: classesData = [] } = useQuery<any[]>({
    queryKey: ['/api/classes'],
  });

  // Location filter states
  const [selectedState, setSelectedState] = useState('all');
  const [selectedDistrict, setSelectedDistrict] = useState('all');
  const [selectedMandal, setSelectedMandal] = useState('all');
  const [selectedVillage, setSelectedVillage] = useState('all');
  const [selectedCenter, setSelectedCenter] = useState('all');

  // Fetch location data for filters
  const { data: states = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/states'],
  });

  const { data: districts = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/districts'],
  });

  const { data: mandals = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/mandals'],
  });

  const { data: villages = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/villages'],
  });

  const { data: soCenters = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/so-centers'],
  });

  // Delete student mutation
  const deleteStudentMutation = useMutation({
    mutationFn: (studentId: string) => {
      return apiRequest('DELETE', `/api/students/${studentId}`);
    },
    onSuccess: () => {
      toast({
        title: 'Student Deleted',
        description: 'Student has been successfully deleted.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      setDeleteDialogOpen(false);
      setStudentToDelete(null);
    },
    onError: (error: any) => {
      toast({
        title: 'Delete Failed',
        description: error.message || 'Failed to delete student. Please try again.',
        variant: 'destructive',
      });
    },
  });

  // Filter location data based on selections
  const filteredDistricts = (districts as any[]).filter((district: any) => 
    selectedState === 'all' || district.stateId === selectedState
  );

  const filteredMandals = (mandals as any[]).filter((mandal: any) => 
    selectedDistrict === 'all' || mandal.districtId === selectedDistrict
  );

  const filteredVillages = (villages as any[]).filter((village: any) => 
    selectedMandal === 'all' || village.mandalId === selectedMandal
  );

  const filteredSoCenters = (soCenters as any[]).filter((center: any) => {
    if (selectedState === 'all' && selectedDistrict === 'all' && selectedMandal === 'all' && selectedVillage === 'all') {
      return true;
    }

    // Use the joined location data from the center
    const stateMatch = selectedState === 'all' || center.stateName === (states as any[]).find(s => s.id === selectedState)?.name;
    const districtMatch = selectedDistrict === 'all' || center.districtName === (districts as any[]).find(d => d.id === selectedDistrict)?.name;
    const mandalMatch = selectedMandal === 'all' || center.mandalName === (mandals as any[]).find(m => m.id === selectedMandal)?.name;
    const villageMatch = selectedVillage === 'all' || center.villageName === (villages as any[]).find(v => v.id === selectedVillage)?.name;

    return stateMatch && districtMatch && mandalMatch && villageMatch;
  });

  // Filter students
  const filteredStudents = students.filter((student: Student) => {
    const matchesSearch = student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         student.studentId?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         student.parentPhone.includes(searchTerm) ||
                         student.fatherName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesClass = classFilter === 'all' || student.classId === classFilter;
    const matchesCenter = selectedCenter === 'all' || student.soCenterId === selectedCenter;

    // Location-based filtering through student's SO center
    let matchesLocation = true;
    if (selectedState !== 'all' || selectedDistrict !== 'all' || selectedMandal !== 'all' || selectedVillage !== 'all') {
      const studentCenter = (soCenters as any[]).find(c => c.id === student.soCenterId);
      if (studentCenter) {
        const stateMatch = selectedState === 'all' || studentCenter.stateName === (states as any[]).find(s => s.id === selectedState)?.name;
        const districtMatch = selectedDistrict === 'all' || studentCenter.districtName === (districts as any[]).find(d => d.id === selectedDistrict)?.name;
        const mandalMatch = selectedMandal === 'all' || studentCenter.mandalName === (mandals as any[]).find(m => m.id === selectedMandal)?.name;
        const villageMatch = selectedVillage === 'all' || studentCenter.villageName === (villages as any[]).find(v => v.id === selectedVillage)?.name;
        matchesLocation = stateMatch && districtMatch && mandalMatch && villageMatch;
      } else {
        matchesLocation = false;
      }
    }

    return matchesSearch && matchesClass && matchesCenter && matchesLocation;
  });

  // Pagination
  const totalPages = Math.ceil(filteredStudents.length / studentsPerPage);
  const paginatedStudents = filteredStudents.slice(
    (currentPage - 1) * studentsPerPage,
    currentPage * studentsPerPage
  );

  const handleEdit = (student: Student) => {
    setSelectedStudent(student);
    setIsEditModalOpen(true);
  };

  const handleView = (student: Student) => {
    setSelectedStudent(student);
    setIsViewModalOpen(true);
  };

  const handleDelete = (student: Student) => {
    setStudentToDelete(student);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = () => {
    if (studentToDelete) {
      deleteStudentMutation.mutate(studentToDelete.id);
    }
  };

  const handleStateChange = (stateId: string) => {
    setSelectedState(stateId);
    setSelectedDistrict('all');
    setSelectedMandal('all');
    setSelectedVillage('all');
    setSelectedCenter('all');
  };

  const handleDistrictChange = (districtId: string) => {
    setSelectedDistrict(districtId);
    setSelectedMandal('all');
    setSelectedVillage('all');
    setSelectedCenter('all');
  };

  const handleMandalChange = (mandalId: string) => {
    setSelectedMandal(mandalId);
    setSelectedVillage('all');
    setSelectedCenter('all');
  };

  const handleVillageChange = (villageId: string) => {
    setSelectedVillage(villageId);
    setSelectedCenter('all');
  };

  const clearAllFilters = () => {
    setSearchTerm('');
    setClassFilter('all');
    setSelectedState('all');
    setSelectedDistrict('all');
    setSelectedMandal('all');
    setSelectedVillage('all');
    setSelectedCenter('all');
  };

  const handleExport = () => {
    // Create CSV content
    const headers = ['Student ID', 'Name', 'Father Name', 'Phone', 'Class', 'School', 'Address', 'Created Date'];
    const csvContent = [
      headers.join(','),
      ...filteredStudents.map(student => [
        student.studentId || '',
        student.name,
        student.fatherName,
        student.parentPhone,
        student.classId,
        student.presentSchoolName,
        student.address,
        new Date(student.createdAt || '').toLocaleDateString()
      ].map(field => `"${field}"`).join(','))
    ].join('\n');

    // Download CSV
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `students_${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);

    toast({
      title: 'Export Successful',
      description: `Exported ${filteredStudents.length} students to CSV.`,
    });
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-gray-600">Loading students...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-xl font-semibold text-gray-900">
              Student Management ({filteredStudents.length} students)
            </CardTitle>
            <div className="flex items-center space-x-3">
              <Button onClick={() => setIsAddModalOpen(true)} className="bg-primary">
                <UserPlus className="h-4 w-4 mr-2" />
                Add Student
              </Button>
              <Button 
                variant="outline" 
                onClick={handleExport}
                disabled={filteredStudents.length === 0}
              >
                <Download className="h-4 w-4 mr-2" />
                Export CSV
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent>
          {/* Location Filters Section */}
          <div className="bg-gray-50 p-4 rounded-lg border-b pb-6 mb-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                <MapPin className="h-5 w-5 mr-2 text-blue-600" />
                Location Filters
              </h3>
              <Button variant="outline" onClick={clearAllFilters} size="sm">
                <Filter className="h-4 w-4 mr-2" />
                Clear All
              </Button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
              {/* State Filter */}
              <Select onValueChange={handleStateChange} value={selectedState}>
                <SelectTrigger className="bg-white border-2 border-gray-300 focus:border-blue-500">
                  <SelectValue placeholder="ðŸ›ï¸ Select State" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All States</SelectItem>
                  {(states as any[]).map((state: any) => (
                    <SelectItem key={state.id} value={state.id}>
                      {state.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* District Filter */}
              <Select 
                onValueChange={handleDistrictChange} 
                value={selectedDistrict}
                disabled={selectedState === 'all'}
              >
                <SelectTrigger className="bg-white border-2 border-gray-300 focus:border-blue-500">
                  <SelectValue placeholder="ðŸ˜ï¸ Select District" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Districts</SelectItem>
                  {filteredDistricts.map((district: any) => (
                    <SelectItem key={district.id} value={district.id}>
                      {district.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* Mandal Filter */}
              <Select 
                onValueChange={handleMandalChange} 
                value={selectedMandal}
                disabled={selectedDistrict === 'all'}
              >
                <SelectTrigger className="bg-white border-2 border-gray-300 focus:border-blue-500">
                  <SelectValue placeholder="ðŸ›ï¸ Select Mandal" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Mandals</SelectItem>
                  {filteredMandals.map((mandal: any) => (
                    <SelectItem key={mandal.id} value={mandal.id}>
                      {mandal.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* Village Filter */}
              <Select 
                onValueChange={handleVillageChange} 
                value={selectedVillage}
                disabled={selectedMandal === 'all'}
              >
                <SelectTrigger className="bg-white border-2 border-gray-300 focus:border-blue-500">
                  <SelectValue placeholder="ðŸ˜ï¸ Select Village" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Villages</SelectItem>
                  {filteredVillages.map((village: any) => (
                    <SelectItem key={village.id} value={village.id}>
                      {village.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* SO Center Filter */}
              <Select 
                onValueChange={setSelectedCenter} 
                value={selectedCenter}
              >
                <SelectTrigger className="bg-white border-2 border-gray-300 focus:border-blue-500">
                  <SelectValue placeholder="ðŸ¢ Select SO Center" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All SO Centers</SelectItem>
                  {filteredSoCenters.map((center: any) => (
                    <SelectItem key={center.id} value={center.id}>
                      {center.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Basic Filters */}
          <div className="flex items-center space-x-4 mb-6">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-3 text-gray-400" size={16} />
              <Input
                placeholder="Search by name, student ID, phone, or father's name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>

            <Select value={classFilter} onValueChange={setClassFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="All Classes" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Classes</SelectItem>
                {classesData.map((cls: any) => (
                  <SelectItem key={cls.id} value={cls.id}>
                    {cls.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Students Table */}
          <div className="overflow-x-auto">
            {paginatedStudents.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-gray-500">No students found</p>
              </div>
            ) : (
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Student
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Contact
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Academic
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {paginatedStudents.map((student: Student) => (
                    <tr key={student.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 h-10 w-10">
                            <div className="h-10 w-10 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 flex items-center justify-center text-white font-medium">
                              {student.name.charAt(0).toUpperCase()}
                            </div>
                          </div>
                          <div className="ml-4">
                            <div className="text-sm font-medium text-gray-900">
                              {student.name}
                            </div>
                            <div className="text-sm text-gray-500">
                              ID: {student.studentId || 'N/A'}
                            </div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{student.fatherName}</div>
                        <div className="text-sm text-gray-500">{student.parentPhone}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {classesData.find((c: any) => c.id === student.classId)?.name || 'Unknown Class'}
                        </div>
                        <div className="text-sm text-gray-500">
                          {student.courseType} â€¢ {student.schoolType}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <Badge 
                          variant={student.isActive ? "default" : "secondary"}
                          className={student.isActive ? "bg-green-100 text-green-800" : "bg-gray-100 text-gray-800"}
                        >
                          {student.isActive ? 'Active' : 'Inactive'}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" className="h-8 w-8 p-0">
                              <MoreVertical className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => handleView(student)}>
                              <Eye className="h-4 w-4 mr-2" />
                              View Details
                            </DropdownMenuItem>
                            <DropdownMenuItem onClick={() => handleEdit(student)}>
                              <Edit className="h-4 w-4 mr-2" />
                              Edit
                            </DropdownMenuItem>
                            <DropdownMenuItem 
                              onClick={() => handleDelete(student)}
                              className="text-red-600"
                            >
                              <Trash2 className="h-4 w-4 mr-2" />
                              Delete
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex items-center justify-between mt-6">
              <div className="text-sm text-gray-500">
                Showing {((currentPage - 1) * studentsPerPage) + 1} to {Math.min(currentPage * studentsPerPage, filteredStudents.length)} of {filteredStudents.length} students
              </div>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  Previous
                </Button>
                <span className="px-3 py-2 text-sm text-gray-700">
                  Page {currentPage} of {totalPages}
                </span>
                <Button
                  variant="outline"
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  Next
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Modals */}
      <AddStudentModal 
        isOpen={isAddModalOpen} 
        onClose={() => setIsAddModalOpen(false)} 
      />

      <EditStudentModal 
        isOpen={isEditModalOpen} 
        onClose={() => setIsEditModalOpen(false)}
        student={selectedStudent}
      />

      {/* <ViewStudentModal 
        isOpen={isViewModalOpen} 
        onClose={() => setIsViewModalOpen(false)}
        student={selectedStudent}
      /> */}

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Student</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete {studentToDelete?.name}? This action cannot be undone.
              All student data including progress and payment records will be permanently removed.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={() => setDeleteDialogOpen(false)}>
              Cancel
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={confirmDelete}
              className="bg-red-600 hover:bg-red-700"
              disabled={deleteStudentMutation.isPending}
            >
              {deleteStudentMutation.isPending ? 'Deleting...' : 'Delete Student'}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

client/src/pages/so-center/ExamResults.t.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Save, FileText, Users, Calculator } from "lucide-react";
import { useLocation } from "wouter";

interface Exam {
  id: string;
  name: string;
  className: string;
  date: string;
  totalQuestions: number;
  totalMarks: number;
  status: string;
}

interface Student {
  id: string;
  name: string;
  studentId: string;
  fatherName: string;
  parentPhone: string;
}

interface ExamQuestion {
  questionNumber: number;
  marks: number;
}

interface StudentResult {
  studentId: string;
  questionResults: {
    questionNumber: number;
    marks: number;
    status: 'not_written' | 'poorly_written' | 'well_written';
  }[];
  totalMarks: number;
  percentage: number;
  remarks?: string;
}

export default function ExamResults() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedExamId, setSelectedExamId] = useState<string>("");
  const [selectedExam, setSelectedExam] = useState<Exam | null>(null);
  const [students, setStudents] = useState<Student[]>([]);
  const [examQuestions, setExamQuestions] = useState<ExamQuestion[]>([]);
  const [studentResults, setStudentResults] = useState<Record<string, StudentResult>>({});
  const [currentStudentIndex, setCurrentStudentIndex] = useState(0);

  // Fetch SO Center's exams
  const { data: examsData, isLoading: examsLoading } = useQuery({
    queryKey: ["/api/so-center/exams"],
    queryFn: () => apiRequest("GET", "/api/so-center/exams"),
  });

  // Ensure exams is always an array
  const exams = Array.isArray(examsData) ? examsData : [];

  // Fetch students for selected exam
  const fetchStudentsForExam = async (examId: string) => {
    try {
      const response = await apiRequest("GET", `/api/so-center/exams/${examId}/students`);
      setStudents(response || []);
    } catch (error) {
      console.error("Error fetching students:", error);
      toast({
        title: "Error",
        description: "Failed to fetch students for exam",
        variant: "destructive",
      });
    }
  };

  // Fetch exam questions
  const fetchExamQuestions = async (examId: string) => {
    try {
      const response = await apiRequest("GET", `/api/so-center/exams/${examId}/questions`);
      // Handle the new API response format
      const questions = response?.questions || response || [];
      setExamQuestions(questions);
    } catch (error) {
      console.error("Error fetching exam questions:", error);
      toast({
        title: "Error",
        description: "Failed to fetch exam questions",
        variant: "destructive",
      });
    }
  };

  // Handle exam selection
  const handleExamSelect = async (examId: string) => {
    setSelectedExamId(examId);
    const exam = exams.find((e: Exam) => e.id === examId);
    setSelectedExam(exam || null);

    if (examId) {
      await Promise.all([
        fetchStudentsForExam(examId),
        fetchExamQuestions(examId)
      ]);

      // Initialize results for all students
      const initialResults: Record<string, StudentResult> = {};
      const studentsArray = Array.isArray(students) ? students : [];
      studentsArray.forEach(student => {
        initialResults[student.id] = {
          studentId: student.id,
          questionResults: examQuestions.map(q => ({
            questionNumber: q.questionNumber,
            marks: 0,
            status: 'not_written' as const
          })),
          totalMarks: 0,
          percentage: 0,
          remarks: ''
        };
      });
      setStudentResults(initialResults);
    }
  };

  // Update question result
  const updateQuestionResult = (
    studentId: string, 
    questionNumber: number, 
    status: 'not_written' | 'poorly_written' | 'well_written'
  ) => {
    setStudentResults(prev => {
      const updated = { ...prev };
      if (!updated[studentId]) {
        updated[studentId] = {
          studentId,
          questionResults: [],
          totalMarks: 0,
          percentage: 0
        };
      }

      const questionIndex = updated[studentId].questionResults.findIndex(
        qr => qr.questionNumber === questionNumber
      );

      if (questionIndex >= 0) {
        updated[studentId].questionResults[questionIndex].status = status;

        // Calculate marks based on status
        const question = examQuestions.find(q => q.questionNumber === questionNumber);
        const maxMarks = question?.marks || 0;

        let marks = 0;
        switch (status) {
          case 'not_written':
            marks = 0;
            break;
          case 'poorly_written':
            marks = Math.floor(maxMarks * 0.3); // 30% marks
            break;
          case 'well_written':
            marks = maxMarks; // Full marks
            break;
        }

        updated[studentId].questionResults[questionIndex].marks = marks;

        // Recalculate total
        const totalMarks = updated[studentId].questionResults.reduce((sum, qr) => sum + qr.marks, 0);
        const maxTotalMarks = examQuestions.reduce((sum, q) => sum + q.marks, 0);
        const percentage = maxTotalMarks > 0 ? Math.round((totalMarks / maxTotalMarks) * 100) : 0;

        updated[studentId].totalMarks = totalMarks;
        updated[studentId].percentage = percentage;
      }

      return updated;
    });
  };

  // Save results mutation
  const saveResultsMutation = useMutation({
    mutationFn: async (data: { examId: string; results: StudentResult[] }) => {
      return apiRequest("POST", "/api/so-center/exam-results", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Exam results saved successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/so-center/exams"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save exam results",
        variant: "destructive",
      });
    },
  });

  // Save all results
  const handleSaveResults = () => {
    if (!selectedExamId || !selectedExam) {
      toast({
        title: "Error",
        description: "Please select an exam first",
        variant: "destructive",
      });
      return;
    }

    const results = Object.values(studentResults);
    saveResultsMutation.mutate({
      examId: selectedExamId,
      results
    });
  };

  // Get current student and result
  const studentsArray = Array.isArray(students) ? students : [];
  const currentStudent = studentsArray[currentStudentIndex];
  const currentResult = currentStudent ? studentResults[currentStudent.id] : null;

  if (examsLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="container mx-auto py-6 space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="outline" onClick={() => setLocation("/so-center/exams")}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Exams
          </Button>
          <div>
            <h1 className="text-2xl font-bold">Exam Results Update</h1>
            <p className="text-muted-foreground">Update student marks for exam questions</p>
          </div>
        </div>

        {selectedExam && (
          <Button onClick={handleSaveResults} disabled={saveResultsMutation.isPending}>
            <Save className="h-4 w-4 mr-2" />
            {saveResultsMutation.isPending ? "Saving..." : "Save All Results"}
          </Button>
        )}
      </div>

      {/* Exam Selection */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Select Exam
          </CardTitle>
          <CardDescription>Choose an exam to update results</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4">
            <div>
              <Label htmlFor="exam-select">Exam</Label>
              <Select value={selectedExamId} onValueChange={handleExamSelect}>
                <SelectTrigger>
                  <SelectValue placeholder="Select an exam" />
                </SelectTrigger>
                <SelectContent>
                  {exams?.map((exam: Exam) => (
                    <SelectItem key={exam.id} value={exam.id}>
                      {exam.name} - {exam.className} ({new Date(exam.date).toLocaleDateString()})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {selectedExam && (
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 p-4 bg-muted rounded-lg">
                <div>
                  <Label className="text-sm text-muted-foreground">Total Questions</Label>
                  <p className="font-semibold">{selectedExam.totalQuestions}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Total Marks</Label>
                  <p className="font-semibold">{selectedExam.totalMarks}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Students</Label>
                  <p className="font-semibold">{students.length}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Date</Label>
                  <p className="font-semibold">{new Date(selectedExam.date).toLocaleDateString()}</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Student Results Entry */}
      {selectedExam && students.length > 0 && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Student List */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                Students ({Array.isArray(students) ? students.length : 0})
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {Array.isArray(students) ? students.map((student, index) => (
                <div
                  key={student.id}
                  className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                    index === currentStudentIndex
                      ? "bg-primary text-primary-foreground"
                      : "hover:bg-muted"
                  }`}
                  onClick={() => setCurrentStudentIndex(index)}
                >
                  <p className="font-medium">{student.name}</p>
                  <p className="text-sm opacity-70">{student.studentId}</p>
                  {studentResults[student.id] && (
                    <div className="flex justify-between items-center mt-1">
                      <Badge variant={index === currentStudentIndex ? "secondary" : "outline"}>
                        {studentResults[student.id].totalMarks}/{selectedExam.totalMarks}
                      </Badge>
                      <span className="text-sm">
                        {studentResults[student.id].percentage}%
                      </span>
                    </div>
                  )}
                </div>
              )) : (
                <p className="text-center text-muted-foreground">No students found</p>
              )}
            </CardContent>
          </Card>

          {/* Question Results Entry */}
          {currentStudent && currentResult && (
            <Card className="lg:col-span-2">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Calculator className="h-5 w-5" />
                  {currentStudent.name} - Results Entry
                </CardTitle>
                <CardDescription>
                  Student ID: {currentStudent.studentId} | Father: {currentStudent.fatherName}
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* Questions */}
                <div className="space-y-4">
                  <h4 className="font-medium">Questions</h4>
                  {examQuestions.map((question) => {
                    const questionResult = currentResult.questionResults.find(
                      qr => qr.questionNumber === question.questionNumber
                    );

                    return (
                      <div key={question.questionNumber} className="p-4 border rounded-lg">
                        <div className="flex justify-between items-center mb-3">
                          <Label className="font-medium">
                            Question {question.questionNumber}
                          </Label>
                          <Badge variant="outline">
                            {questionResult?.marks || 0}/{question.marks} marks
                          </Badge>
                        </div>

                        <div className="grid grid-cols-3 gap-2">
                          {(['not_written', 'poorly_written', 'well_written'] as const).map((status) => (
                            <Button
                              key={status}
                              variant={questionResult?.status === status ? "default" : "outline"}
                              size="sm"
                              onClick={() => updateQuestionResult(
                                currentStudent.id,
                                question.questionNumber,
                                status
                              )}
                              className="text-xs"
                            >
                              {status === 'not_written' && "Not Written (0%)"}
                              {status === 'poorly_written' && "Poorly Written (30%)"}
                              {status === 'well_written' && "Well Written (100%)"}
                            </Button>
                          ))}
                        </div>
                      </div>
                    );
                  })}
                </div>

                {/* Summary */}
                <div className="p-4 bg-muted rounded-lg">
                  <h4 className="font-medium mb-2">Summary</h4>
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <Label className="text-muted-foreground">Total Marks</Label>
                      <p className="font-semibold text-lg">
                        {currentResult.totalMarks}/{selectedExam.totalMarks}
                      </p>
                    </div>
                    <div>
                      <Label className="text-muted-foreground">Percentage</Label>
                      <p className="font-semibold text-lg">
                        {currentResult.percentage}%
                      </p>
                    </div>
                  </div>
                </div>

                {/* Remarks */}
                <div>
                  <Label htmlFor="remarks">Remarks (Optional)</Label>
                  <Textarea
                    id="remarks"
                    value={currentResult.remarks || ""}
                    onChange={(e) => {
                      setStudentResults(prev => ({
                        ...prev,
                        [currentStudent.id]: {
                          ...prev[currentStudent.id],
                          remarks: e.target.value
                        }
                      }));
                    }}
                    placeholder="Add any remarks about the student's performance..."
                    rows={3}
                  />
                </div>

                {/* Navigation */}
                <div className="flex justify-between pt-4">
                  <Button
                    variant="outline"
                    onClick={() => setCurrentStudentIndex(Math.max(0, currentStudentIndex - 1))}
                    disabled={currentStudentIndex === 0}
                  >
                    Previous Student
                  </Button>
                  <Button
                    onClick={() => setCurrentStudentIndex(Math.min(Array.isArray(students) ? students.length - 1 : 0, currentStudentIndex + 1))}
                    disabled={currentStudentIndex === (Array.isArray(students) ? students.length - 1 : 0)}
                  >
                    Next Student
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      )}

      {selectedExam && (!Array.isArray(students) || students.length === 0) && (
        <Card>
          <CardContent className="text-center py-8">
            <p className="text-muted-foreground">No students found for this exam.</p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/so-center/SoCenterExams.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { Calendar, Clock, Users, BookOpen, FileText, ArrowRight, Edit, Save, X } from "lucide-react";

interface Exam {
  id: string;
  name: string;
  title: string;
  className: string;
  date: string;
  totalQuestions: number;
  totalMarks: number;
  status: string;
  description?: string;
}

interface Student {
  id: string;
  name: string;
  studentId: string;
  fatherName: string;
  parentPhone: string;
}

interface ExamQuestion {
  id: string;
  questionNumber: number;
  text: string;
  marks: number;
  type: string;
}

interface StudentMarks {
  studentId: string;
  marks: Array<{
    questionNo: number;
    score: number;
    maxMarks: number;
  }>;
  performance: string[];
  totalScore: number;
}

export default function SoCenterExams() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Modal state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedExam, setSelectedExam] = useState<Exam | null>(null);
  const [students, setStudents] = useState<Student[]>([]);
  const [examQuestions, setExamQuestions] = useState<ExamQuestion[]>([]);
  const [studentMarks, setStudentMarks] = useState<Record<string, StudentMarks>>({});

  // Fetch SO Center's exams
  const { data: examsData, isLoading, error } = useQuery({
    queryKey: ["/api/so-center/exams"],
    queryFn: () => apiRequest("GET", "/api/so-center/exams"),
  });

  // Ensure exams is always an array
  const exams = Array.isArray(examsData) ? examsData : [];

  // Fetch students and questions for modal
  const fetchExamData = async (examId: string) => {
    try {
      console.log('ðŸ” Fetching exam data for:', examId);

      const [studentsResponse, questionsResponse] = await Promise.all([
        apiRequest("GET", `/api/so-center/exams/${examId}/students`),
        apiRequest("GET", `/api/exams/${examId}/questions`)
      ]);

      console.log('ðŸ“Š Students response:', studentsResponse);
      console.log('ðŸ“Š Questions response:', questionsResponse);

      const studentsData = Array.isArray(studentsResponse) ? studentsResponse : [];
      const questionsData = Array.isArray(questionsResponse) ? questionsResponse : [];

      console.log('ðŸ“Š Processed students:', studentsData.length);
      console.log('ðŸ“Š Processed questions:', questionsData.length);

      setStudents(studentsData);
      setExamQuestions(questionsData);

      // Initialize marks for each student
      const initialMarks: Record<string, StudentMarks> = {};
      studentsData.forEach((student: Student) => {
        initialMarks[student.id] = {
          studentId: student.id,
          marks: questionsData.map((q: ExamQuestion, index: number) => ({
            questionNo: q.questionNumber || (index + 1),
            score: 0,
            maxMarks: q.marks || 1
          })),
          performance: questionsData.map(() => 'not_attempted'),
          totalScore: 0
        };
      });
      setStudentMarks(initialMarks);

      console.log('âœ… Exam data loaded successfully');
    } catch (error) {
      console.error('âŒ Error fetching exam data:', error);
      toast({
        title: "Error",
        description: "Failed to fetch exam data",
        variant: "destructive",
      });
    }
  };

  const handleUpdateResults = async (exam: Exam) => {
    setSelectedExam(exam);
    setIsModalOpen(true);
    await fetchExamData(exam.id);
  };

  // Update student marks
  const updateStudentMark = (studentId: string, questionIndex: number, score: number) => {
    setStudentMarks(prev => {
      const updated = { ...prev };
      if (updated[studentId]) {
        updated[studentId].marks[questionIndex].score = score;
        // Auto-calculate total
        updated[studentId].totalScore = updated[studentId].marks.reduce((sum, mark) => sum + mark.score, 0);
      }
      return updated;
    });
  };

  // Update performance level
  const updatePerformance = (studentId: string, questionIndex: number, performance: string) => {
    setStudentMarks(prev => {
      const updated = { ...prev };
      if (updated[studentId]) {
        updated[studentId].performance[questionIndex] = performance;

        // Auto-calculate score based on performance
        const maxMarks = updated[studentId].marks[questionIndex].maxMarks;
        let score = 0;
        switch (performance) {
          case 'not_attempted':
            score = 0;
            break;
          case 'poor':
            score = Math.ceil(maxMarks * 0.3); // 30% of max marks
            break;
          case 'good':
            score = maxMarks; // Full marks
            break;
        }
        updated[studentId].marks[questionIndex].score = score;
        updated[studentId].totalScore = updated[studentId].marks.reduce((sum, mark) => sum + mark.score, 0);
      }
      return updated;
    });
  };

  // Save results mutation
  const saveResultsMutation = useMutation({
    mutationFn: async (data: { examId: string; students: StudentMarks[] }) => {
      return apiRequest("POST", `/api/exams/${data.examId}/results/update`, { students: data.students });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Exam results updated successfully",
      });
      setIsModalOpen(false);
      setSelectedExam(null);
      queryClient.invalidateQueries({ queryKey: ["/api/so-center/exams"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update exam results",
        variant: "destructive",
      });
    },
  });

  const handleSaveResults = () => {
    if (!selectedExam) return;

    const studentsData = Object.values(studentMarks);
    saveResultsMutation.mutate({
      examId: selectedExam.id,
      students: studentsData
    });
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
      case 'ongoing':
        return 'bg-green-100 text-green-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto py-6">
        <Card>
          <CardContent className="text-center py-8">
            <p className="text-destructive">Error loading exams. Please try again.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Your Exams</h1>
          <p className="text-muted-foreground">View and manage exam results</p>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <BookOpen className="h-4 w-4 text-blue-500" />
              <div>
                <p className="text-sm font-medium">Total Exams</p>
                <p className="text-2xl font-bold">{exams.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Calendar className="h-4 w-4 text-green-500" />
              <div>
                <p className="text-sm font-medium">Completed</p>
                <p className="text-2xl font-bold">
                  {exams.filter((exam: Exam) => exam.status === 'completed').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Clock className="h-4 w-4 text-yellow-500" />
              <div>
                <p className="text-sm font-medium">Ongoing</p>
                <p className="text-2xl font-bold">
                  {exams.filter((exam: Exam) => exam.status === 'ongoing' || exam.status === 'active').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Users className="h-4 w-4 text-purple-500" />
              <div>
                <p className="text-sm font-medium">This Month</p>
                <p className="text-2xl font-bold">
                  {exams.filter((exam: Exam) => {
                    const examDate = new Date(exam.date);
                    const now = new Date();
                    return examDate.getMonth() === now.getMonth() && examDate.getFullYear() === now.getFullYear();
                  }).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Exams Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Exam List
          </CardTitle>
          <CardDescription>View and update exam results</CardDescription>
        </CardHeader>
        <CardContent>
          {exams.length > 0 ? (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Exam Name</TableHead>
                  <TableHead>Class</TableHead>
                  <TableHead>Date</TableHead>
                  <TableHead>Questions</TableHead>
                  <TableHead>Total Marks</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {exams.map((exam: Exam) => (
                  <TableRow key={exam.id}>
                    <TableCell>
                      <div>
                        <p className="font-medium">{exam.title || exam.name}</p>
                        {exam.description && (
                          <p className="text-sm text-muted-foreground">{exam.description}</p>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant="outline">{exam.className}</Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        {new Date(exam.date).toLocaleDateString()}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <FileText className="h-3 w-3" />
                        {exam.totalQuestions}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <span className="font-medium">{exam.totalMarks}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge className={getStatusColor(exam.status)}>
                        {exam.status}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Button
                        size="sm"
                        onClick={() => handleUpdateResults(exam)}
                        className="flex items-center gap-1"
                      >
                        <Edit className="h-3 w-3" />
                        Update Results
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          ) : (
            <div className="text-center py-8">
              <BookOpen className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No exams found</p>
              <p className="text-sm text-muted-foreground mt-1">
                Exams will appear here once they are assigned to your SO Center
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Marks Entry Modal */}
      <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
        <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Edit className="h-5 w-5" />
              Update Exam Results: {selectedExam?.title || selectedExam?.name}
            </DialogTitle>
            <DialogDescription>
              Enter marks and performance levels for each student. Auto-calculation is enabled.
            </DialogDescription>
          </DialogHeader>

          <ScrollArea className="max-h-[70vh] w-full">
            <div className="space-y-6 p-4">
              {students.length > 0 && examQuestions.length > 0 ? (
                students.map((student) => {
                  const marks = studentMarks[student.id];
                  if (!marks) return null;

                  return (
                    <Card key={student.id} className="p-4">
                      <div className="space-y-4">
                        {/* Student Header */}
                        <div className="flex justify-between items-center border-b pb-2">
                          <div>
                            <h4 className="font-semibold">{student.name}</h4>
                            <p className="text-sm text-muted-foreground">ID: {student.studentId} | Father: {student.fatherName}</p>
                          </div>
                          <div className="text-right">
                            <p className="text-lg font-bold">Total: {marks.totalScore}/{selectedExam?.totalMarks}</p>
                            <p className="text-sm text-muted-foreground">
                              {selectedExam?.totalMarks ? Math.round((marks.totalScore / selectedExam.totalMarks) * 100) : 0}%
                            </p>
                          </div>
                        </div>

                        {/* Questions Grid */}
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {examQuestions.map((question, qIndex) => (
                            <div key={question.id} className="border rounded-lg p-3 space-y-2">
                              <div className="flex justify-between items-center">
                                <Label className="font-medium">Q{question.questionNumber}</Label>
                                <Badge variant="outline">{question.marks} marks</Badge>
                              </div>

                              <div className="space-y-2">
                                <div>
                                  <Label htmlFor={`score-${student.id}-${qIndex}`} className="text-xs">Score</Label>
                                  <Input
                                    id={`score-${student.id}-${qIndex}`}
                                    type="number"
                                    min="0"
                                    max={question.marks}
                                    value={marks.marks[qIndex]?.score || 0}
                                    onChange={(e) => {
                                      const score = Math.min(Number(e.target.value), question.marks);
                                      updateStudentMark(student.id, qIndex, score);
                                    }}
                                    className="h-8"
                                  />
                                </div>

                                <div>
                                  <Label htmlFor={`performance-${student.id}-${qIndex}`} className="text-xs">Performance</Label>
                                  <Select
                                    value={marks.performance[qIndex] || 'not_attempted'}
                                    onValueChange={(value) => updatePerformance(student.id, qIndex, value)}
                                  >
                                    <SelectTrigger className="h-8">
                                      <SelectValue />
                                    </SelectTrigger>
                                    <SelectContent>
                                      <SelectItem value="not_attempted">Not Attempted</SelectItem>
                                      <SelectItem value="poor">Poor</SelectItem>
                                      <SelectItem value="good">Good</SelectItem>
                                    </SelectContent>
                                  </Select>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    </Card>
                  );
                })
              ) : (
                <div className="text-center py-8">
                  <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
                  <p className="text-muted-foreground">Loading exam data...</p>
                </div>
              )}
            </div>
          </ScrollArea>

          <div className="flex justify-end gap-2 pt-4 border-t">
            <Button variant="outline" onClick={() => setIsModalOpen(false)}>
              <X className="h-4 w-4 mr-2" />
              Cancel
            </Button>
            <Button 
              onClick={handleSaveResults}
              disabled={saveResultsMutation.isPending}
            >
              {saveResultsMutation.isPending ? (
                <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
              ) : (
                <Save className="h-4 w-4 mr-2" />
              )}
              Save Results
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

