import { supabaseAdmin } from './supabaseClient.js';
import { db } from './db.js';

/**
 * Clean all dummy data from Supabase Auth and PostgreSQL database
 * Preserves table structures and logic, removes only data
 * Keeps admin user for system functionality
 */
export async function cleanAllSupabaseData() {
  console.log('ðŸ§¹ Starting complete Supabase dummy data cleanup...');

  try {
    // Step 1: Delete all non-admin users from Supabase Auth
    console.log('ðŸ”¥ Deleting all non-admin users from Supabase Auth...');

    // Get all users from Supabase Auth
    const { data: users, error: listError } = await supabaseAdmin.auth.admin.listUsers();

    if (listError) {
      console.error('âŒ Error listing Supabase Auth users:', listError);
      throw listError;
    }

    console.log(`ðŸ“Š Found ${users.users.length} users in Supabase Auth`);

    const adminEmail = 'navanidhi.care@gmail.com';
    let deletedCount = 0;

    // Delete each non-admin user from Supabase Auth
    for (const user of users.users) {
      if (user.email !== adminEmail) {
        const { error: deleteError } = await supabaseAdmin.auth.admin.deleteUser(user.id);
        if (deleteError) {
          console.error(`âŒ Error deleting user ${user.email}:`, deleteError);
        } else {
          console.log(`âœ… Deleted Supabase Auth user: ${user.email}`);
          deletedCount++;
        }
      } else {
        console.log(`ðŸ”’ Preserved admin user: ${user.email}`);
      }
    }

    // Step 2: Clean all PostgreSQL tables
    console.log('ðŸ—‘ï¸ Cleaning all PostgreSQL tables...');

    await db.transaction(async (tx) => {
      console.log('ðŸ—‘ï¸ Cleaning PostgreSQL tables while preserving admin data...');

      // Delete in reverse dependency order to avoid foreign key constraints

      // Student and academic related data (all dummy data)
      await tx.execute({ sql: 'DELETE FROM topic_progress' });
      await tx.execute({ sql: 'DELETE FROM tuition_progress' });  
      await tx.execute({ sql: 'DELETE FROM homework_activities' });
      await tx.execute({ sql: 'DELETE FROM attendance' });
      await tx.execute({ sql: 'DELETE FROM student_siblings' });
      await tx.execute({ sql: 'DELETE FROM students' });
      console.log('ðŸ”¥ Student data cleaned');

      // Teaching and academic data (all dummy data)
      await tx.execute({ sql: 'DELETE FROM teacher_daily_records' });
      await tx.execute({ sql: 'DELETE FROM teacher_classes' });
      await tx.execute({ sql: 'DELETE FROM teacher_subjects' });
      await tx.execute({ sql: 'DELETE FROM teachers' });
      console.log('ðŸ”¥ Teacher data cleaned');

      // Payment and transaction data (all dummy data)
      await tx.execute({ sql: 'DELETE FROM payments' });
      await tx.execute({ sql: 'DELETE FROM wallet_transactions' });
      await tx.execute({ sql: 'DELETE FROM withdrawal_requests' });
      await tx.execute({ sql: 'DELETE FROM commission_transactions' });
      await tx.execute({ sql: 'DELETE FROM commission_wallets' });
      await tx.execute({ sql: 'DELETE FROM product_orders' });
      console.log('ðŸ”¥ Payment data cleaned');

      // Fee management data (all dummy data)
      await tx.execute({ sql: 'DELETE FROM class_fees' });
      await tx.execute({ sql: 'DELETE FROM monthly_fee_schedule' });
      await tx.execute({ sql: 'DELETE FROM fee_calculation_history' });
      console.log('ðŸ”¥ Fee data cleaned');

      // SO Centers (all dummy data)
      await tx.execute({ sql: 'DELETE FROM so_centers' });
      console.log('ðŸ”¥ SO Centers cleaned');

      // Delete non-admin users only
      const adminEmail = 'navanidhi.care@gmail.com';
      await tx.execute({ 
        sql: `DELETE FROM users WHERE email != '${adminEmail}'` 
      });
      console.log('ðŸ”¥ Non-admin users cleaned');

      // Academic structure (keep some basic structure for admin functionality)
      await tx.execute({ sql: 'DELETE FROM topics' });
      await tx.execute({ sql: 'DELETE FROM chapters' });
      await tx.execute({ sql: 'DELETE FROM subjects' });
      await tx.execute({ sql: 'DELETE FROM classes' });
      console.log('ðŸ”¥ Academic structure cleaned');

      // Address hierarchy (can be rebuilt from UI)
      await tx.execute({ sql: 'DELETE FROM villages' });
      await tx.execute({ sql: 'DELETE FROM mandals' });
      await tx.execute({ sql: 'DELETE FROM districts' });
      await tx.execute({ sql: 'DELETE FROM states' });
      console.log('ðŸ”¥ Address data cleaned');

      // Products and settings (keep system settings)
      await tx.execute({ sql: 'DELETE FROM products' });
      await tx.execute({ sql: 'DELETE FROM student_counter' });
      await tx.execute({ sql: 'DELETE FROM nearby_schools' });
      await tx.execute({ sql: 'DELETE FROM nearby_tuitions' });
      console.log('ðŸ”¥ Product data cleaned');

      console.log('âœ… All dummy data cleaned from PostgreSQL tables');
    });

    console.log('ðŸŽ‰ Complete Supabase dummy data cleanup successful!');
    console.log('ðŸ“ All table structures and logic preserved');
    console.log('ðŸ”’ Admin user preserved for system access');
    console.log('ðŸ”‘ Ready for fresh data entry');

    return {
      success: true,
      message: 'All Supabase dummy data cleaned successfully',
      usersDeleted: deletedCount,
      adminPreserved: true
    };

  } catch (error) {
    console.error('âŒ Supabase data cleanup failed:', error);
    throw error;
  }
}

server/createAdminUser.ts.content:
import { supabaseAdmin } from './supabaseClient';
import { storage } from './storage';

export async function createAdminUser() {
  try {
    console.log('ðŸ”§ Creating admin user in Supabase Auth...');

    const adminEmail = 'navanidhi.care@gmail.com';
    const adminPassword = 'Psd@1986';

    // Check if user already exists in Supabase Auth
    const { data: existingUsers } = await supabaseAdmin.auth.admin.listUsers();
    const existingUser = existingUsers.users?.find(user => user.email === adminEmail);

    let supabaseUser;

    if (existingUser) {
      console.log('âœ… Admin user already exists in Supabase Auth:', existingUser.id);
      supabaseUser = existingUser;
    } else {
      // Create user in Supabase Auth
      const { data: authUser, error: authError } = await supabaseAdmin.auth.admin.createUser({
        email: adminEmail,
        password: adminPassword,
        email_confirm: true,
        user_metadata: {
          name: 'Admin User',
          role: 'admin'
        }
      });

      if (authError) {
        console.error('âŒ Error creating admin user in Supabase Auth:', authError);
        throw authError;
      }

      supabaseUser = authUser.user;
      console.log('âœ… Admin user created in Supabase Auth:', supabaseUser?.id);
    }

    if (!supabaseUser?.id) {
      throw new Error('Failed to get Supabase user ID');
    }

    // Check if user exists in our database
    const existingDbUser = await storage.getUserByEmail(adminEmail);

    if (!existingDbUser) {
      console.log('ðŸ”§ Creating admin user in database...');

      // Create user in our database
      const adminUser = await storage.createUser({
        email: adminEmail,
        role: 'admin',
        name: 'Admin User',
        isActive: true,
        password: 'supabase_auth' // Placeholder since we use Supabase Auth
      });

      console.log('âœ… Admin user created in database:', adminUser.id);

      // Update the Supabase user with our database user ID in metadata
      await supabaseAdmin.auth.admin.updateUserById(supabaseUser.id, {
        user_metadata: {
          database_user_id: adminUser.id,
          role: 'admin',
          name: 'Admin User'
        }
      });

      console.log('âœ… Supabase user metadata updated with database ID');
    } else {
      console.log('âœ… Admin user already exists in database:', existingDbUser.id);
    }

    console.log('ðŸŽ‰ Admin user setup complete!');
    console.log('ðŸ“§ Email:', adminEmail);
