import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Package, DollarSign, Percent, FileText } from 'lucide-react';

interface Product {
  id: string;
  name: string;
  description: string | null;
  requirements: string | null;
  price: string;
  commissionPercentage: string;
  isActive: boolean;
  createdAt: string;
}

interface ProductsListProps {
  userRole: 'so_center' | 'agent';
}

export function ProductsList({ userRole }: ProductsListProps) {
  // Fetch active products
  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: [`/api/${userRole}/products`],
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {Array.from({ length: 3 }).map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader>
              <div className="h-6 bg-gray-200 rounded"></div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="h-4 bg-gray-200 rounded"></div>
                <div className="h-4 bg-gray-200 rounded w-2/3"></div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="text-center py-8">
        <Package className="h-16 w-16 text-gray-300 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-gray-900 mb-2">No Products Available</h3>
        <p className="text-gray-600">
          {userRole === 'so_center' 
            ? 'No products are currently available for sale at your center.'
            : 'No products are currently available for commission-based sales.'
          }
        </p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {products.map((product) => (
        <Card key={product.id} className="group hover:shadow-md transition-shadow">
          <CardHeader>
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <CardTitle className="text-lg text-gray-900 group-hover:text-blue-600 transition-colors">
                  {product.name}
                </CardTitle>
                {product.description && (
                  <p className="text-sm text-gray-600 mt-1 line-clamp-2">{product.description}</p>
                )}
              </div>
              <Badge variant="secondary" className="ml-2">
                Active
              </Badge>
            </div>
          </CardHeader>

          <CardContent>
            <div className="space-y-4">
              {/* Price and Commission */}
              <div className="grid grid-cols-2 gap-3">
                <div className="bg-blue-50 rounded-lg p-3 border border-blue-100">
                  <div className="flex items-center gap-2 mb-1">
                    <DollarSign className="h-4 w-4 text-blue-600" />
                    <span className="text-xs font-medium text-blue-600 uppercase tracking-wide">Price</span>
                  </div>
                  <div className="font-bold text-blue-700">â‚¹{Number(product.price).toLocaleString()}</div>
                </div>

                <div className="bg-green-50 rounded-lg p-3 border border-green-100">
                  <div className="flex items-center gap-2 mb-1">
                    <Percent className="h-4 w-4 text-green-600" />
                    <span className="text-xs font-medium text-green-600 uppercase tracking-wide">
                      {userRole === 'agent' ? 'Commission' : 'Margin'}
                    </span>
                  </div>
                  <div className="font-bold text-green-700">{product.commissionPercentage}%</div>
                </div>
              </div>

              {/* Requirements */}
              {product.requirements && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                  <div className="flex items-center gap-2 mb-2">
                    <FileText className="h-4 w-4 text-yellow-600" />
                    <span className="text-xs font-medium text-yellow-700 uppercase tracking-wide">Requirements</span>
                  </div>
                  <p className="text-sm text-yellow-800 line-clamp-2">{product.requirements}</p>
                </div>
              )}

              {/* Earnings Potential (for agents) */}
              {userRole === 'agent' && (
                <div className="bg-purple-50 border border-purple-200 rounded-lg p-3">
                  <div className="text-xs text-purple-600 font-medium uppercase tracking-wide mb-1">
                    Commission Earnings
                  </div>
                  <div className="font-bold text-purple-700">
                    â‚¹{(Number(product.price) * Number(product.commissionPercentage) / 100).toLocaleString()} per sale
                  </div>
                </div>
              )}

              {/* Action Button */}
              <Button 
                className="w-full mt-4" 
                variant={userRole === 'agent' ? 'default' : 'outline'}
              >
                {userRole === 'agent' ? 'Promote Product' : 'View Details'}
              </Button>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

client/src/components/students/EditStude.content:
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { z } from 'zod';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useState, useEffect } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { User, GraduationCap, MapPin, Users } from 'lucide-react';
import type { Student } from '@shared/schema';

// Edit student schema - simplified version of the main schema
const editStudentSchema = z.object({
  name: z.string().min(1, 'Student name is required'),
  fatherName: z.string().min(1, 'Father name is required'),
  motherName: z.string().min(1, 'Mother name is required'),
  fatherMobile: z.string().min(10, 'Valid father mobile number required'),
  motherMobile: z.string().optional(),
  fatherQualification: z.string().optional(),
  motherQualification: z.string().optional(),
  gender: z.enum(['male', 'female', 'other']),
  dateOfBirth: z.string().min(1, 'Date of birth is required'),
  presentSchoolName: z.string().min(1, 'Present school name is required'),
  schoolType: z.enum(['government', 'private']),
  villageId: z.string().min(1, 'Village selection is required'),
  address: z.string().min(1, 'Complete address is required'),
  landmark: z.string().optional(),
  classId: z.string().min(1, 'Class is required'),
  courseType: z.enum(['monthly', 'yearly']),
  parentPhone: z.string().min(10, 'Parent phone is required'),
  parentName: z.string().optional(),
});

type EditStudentFormData = z.infer<typeof editStudentSchema>;

interface EditStudentModalProps {
  isOpen: boolean;
  onClose: () => void;
  student: Student | null;
}

export function EditStudentModal({ isOpen, onClose, student }: EditStudentModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedState, setSelectedState] = useState('');
  const [selectedDistrict, setSelectedDistrict] = useState('');
  const [selectedMandal, setSelectedMandal] = useState('');

  // Fetch data for dropdowns
  const { data: classesData = [] } = useQuery({
    queryKey: ['/api/classes'],
    enabled: isOpen,
  });

  const { data: states = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/states'],
    enabled: isOpen,
  });

  const { data: districts = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/districts', selectedState],
    queryFn: async () => {
      if (!selectedState) return [];
      const response = await apiRequest('GET', `/api/admin/addresses/districts/${selectedState}`);
      return await response.json();
    },
    enabled: !!selectedState && isOpen,
  });

  const { data: mandals = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/mandals', selectedDistrict],
    queryFn: async () => {
      if (!selectedDistrict) return [];
      const response = await apiRequest('GET', `/api/admin/addresses/mandals/${selectedDistrict}`);
      return await response.json();
    },
    enabled: !!selectedDistrict && isOpen,
  });

  const { data: villages = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/addresses/villages', selectedMandal],
    queryFn: async () => {
      if (!selectedMandal) return [];
      const response = await apiRequest('GET', `/api/admin/addresses/villages/${selectedMandal}`);
      return await response.json();
    },
    enabled: !!selectedMandal && isOpen,
  });

  const form = useForm<EditStudentFormData>({
    resolver: zodResolver(editStudentSchema),
    defaultValues: {
      name: '',
      fatherName: '',
      motherName: '',
      fatherMobile: '',
      motherMobile: '',
      fatherQualification: '',
      motherQualification: '',
      gender: 'male',
      dateOfBirth: '',
      presentSchoolName: '',
      schoolType: 'government',
      villageId: '',
      address: '',
      landmark: '',
      classId: '',
      courseType: 'monthly',
      parentPhone: '',
      parentName: '',
    },
  });

  // Populate form when student data changes
  useEffect(() => {
    if (student && isOpen) {
      form.reset({
        name: student.name || '',
        fatherName: student.fatherName || '',
        motherName: student.motherName || '',
        fatherMobile: student.fatherMobile || '',
        motherMobile: student.motherMobile || '',
        fatherQualification: student.fatherQualification || '',
        motherQualification: student.motherQualification || '',
        gender: student.gender || 'male',
        dateOfBirth: student.dateOfBirth || '',
        presentSchoolName: student.presentSchoolName || '',
        schoolType: student.schoolType || 'government',
        villageId: student.villageId || '',
        address: student.address || '',
        landmark: student.landmark || '',
        classId: student.classId || '',
        courseType: student.courseType || 'monthly',
        parentPhone: student.parentPhone || '',
        parentName: student.parentName || '',
      });
    }
  }, [student, isOpen, form]);

  const updateStudentMutation = useMutation({
    mutationFn: (data: EditStudentFormData) => {
      return apiRequest('PUT', `/api/students/${student?.id}`, data);
    },
    onSuccess: () => {
      toast({
        title: 'Student Updated Successfully',
        description: 'Student information has been updated.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/students'] });
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Update Failed',
        description: error.message || 'Failed to update student. Please try again.',
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: EditStudentFormData) => {
    updateStudentMutation.mutate(data);
  };

  const classes = Array.isArray(classesData) && classesData.length > 0 ? classesData.map((cls: any) => ({
    value: cls.id,
    label: cls.name
  })) : [];

  const qualificationOptions = [
    'Illiterate',
    'Primary School',
    'Middle School', 
    '10th Class',
    '12th Class',
    'Diploma',
    'Graduation',
    'Post Graduation',
    'PhD',
    'Other'
  ];

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[800px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Edit Student Information
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">

            {/* Basic Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <User className="h-4 w-4" />
                  Basic Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Student Full Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter student full name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="gender"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Gender *</FormLabel>
                        <FormControl>
                          <RadioGroup
                            onValueChange={field.onChange}
                            value={field.value}
                            className="flex space-x-6"
                          >
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="male" id="male" />
                              <Label htmlFor="male">Male</Label>
                            </div>
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="female" id="female" />
                              <Label htmlFor="female">Female</Label>
                            </div>
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="other" id="other" />
                              <Label htmlFor="other">Other</Label>
                            </div>
                          </RadioGroup>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="dateOfBirth"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Date of Birth *</FormLabel>
                      <FormControl>
                        <Input type="date" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </CardContent>
            </Card>

            {/* Family Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Users className="h-4 w-4" />
                  Family Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="fatherName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Father's Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter father's full name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="motherName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Mother's Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter mother's full name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="fatherMobile"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Father's Mobile Number *</FormLabel>
                        <FormControl>
                          <Input placeholder="+91 98765 43210" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="motherMobile"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Mother's Mobile Number</FormLabel>
                        <FormControl>
                          <Input placeholder="+91 98765 43210 (Optional)" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </CardContent>
            </Card>

            {/* Academic Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <GraduationCap className="h-4 w-4" />
                  Academic Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="classId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Class *</FormLabel>
                        <FormControl>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select class" />
                            </SelectTrigger>
                            <SelectContent>
                              {classes.map((cls) => (
                                <SelectItem key={cls.value} value={cls.value}>
                                  {cls.label}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="courseType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Course Type *</FormLabel>
                        <FormControl>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select course type" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="monthly">Monthly Fee</SelectItem>
                              <SelectItem value="yearly">Yearly Fee</SelectItem>
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="presentSchoolName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Present School Name *</FormLabel>
                        <FormControl>
                          <Input placeholder="Enter current school name" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="schoolType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>School Type *</FormLabel>
                        <FormControl>
                          <RadioGroup
                            onValueChange={field.onChange}
                            value={field.value}
                            className="flex space-x-6"
                          >
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="government" id="government" />
                              <Label htmlFor="government">Government</Label>
                            </div>
                            <div className="flex items-center space-x-2">
                              <RadioGroupItem value="private" id="private" />
                              <Label htmlFor="private">Private</Label>
                            </div>
                          </RadioGroup>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </CardContent>
            </Card>

            <div className="flex justify-end space-x-3 pt-6">
              <Button type="button" variant="outline" onClick={onClose}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={updateStudentMutation.isPending}
                className="bg-primary text-white hover:bg-blue-700"
              >
                {updateStudentMutation.isPending ? (
                  'Updating...'
                ) : (
                  'Update Student'
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/students/SuccessSc.content:
import { useEffect, useState } from 'react';
import { Dialog, DialogContent } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { CheckCircle, Download, User, CreditCard, Building2 } from 'lucide-react';
import confetti from 'canvas-confetti';

interface RegistrationResult {
  studentId: string;
  name: string;
  transactionId?: string;
  admissionFeePaid?: boolean;
  amount?: number;
}

interface SuccessScreenProps {
  isOpen: boolean;
  onClose: () => void;
  studentData: RegistrationResult | null;
}

export function SuccessScreen({ isOpen, onClose, studentData }: SuccessScreenProps) {
  const [showCelebration, setShowCelebration] = useState(false);

  useEffect(() => {
    if (isOpen && studentData) {
      // Trigger confetti animation
      setShowCelebration(true);

      // Fire confetti
      const duration = 3000;
      const animationEnd = Date.now() + duration;
      const defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

      const randomInRange = (min: number, max: number) => {
        return Math.random() * (max - min) + min;
      };

      const interval = setInterval(() => {
        const timeLeft = animationEnd - Date.now();

        if (timeLeft <= 0) {
          return clearInterval(interval);
        }

        const particleCount = 50 * (timeLeft / duration);

        confetti({
          ...defaults,
          particleCount,
          origin: { x: randomInRange(0.1, 0.3), y: Math.random() - 0.2 }
        });
        confetti({
          ...defaults,
          particleCount,
          origin: { x: randomInRange(0.7, 0.9), y: Math.random() - 0.2 }
        });
      }, 250);

      // Play success sound (optional)
      try {
        const audio = new Audio('data:audio/wav;base64,');
        audio.volume = 0.3;
        audio.play().catch(() => {
          // Ignore audio play errors
        });
      } catch (error) {
        // Ignore audio errors
      }

      // Stop celebration after animation completes
      setTimeout(() => {
        setShowCelebration(false);
      }, 3000);
    }
  }, [isOpen, studentData]);

  const handleDownloadReceipt = () => {
    if (!studentData) return;

    // Create receipt content
    const receiptContent = `
NAVANIDHI ACADEMY
Student Registration Receipt

Registration Successful!
------------------------
Student Name: ${studentData.name}
Student ID: ${studentData.studentId}
Registration Date: ${new Date().toLocaleDateString()}
${studentData.admissionFeePaid ? `Transaction ID: ${studentData.transactionId}` : ''}
${studentData.admissionFeePaid ? `Admission Fee: â‚¹${studentData.amount}` : ''}

Thank you for choosing Navanidhi Academy!
Contact: support@navanidhi.com
    `.trim();

    // Create and download text file
    const blob = new Blob([receiptContent], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `receipt_${studentData.studentId}_${new Date().toISOString().split('T')[0]}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  if (!studentData) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md" aria-describedby="success-description">
        <div className="text-center space-y-6">
          {/* Success Icon */}
          <div className="relative">
            <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto">
              <CheckCircle className="h-12 w-12 text-green-600" />
            </div>
            {showCelebration && (
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="animate-ping w-20 h-20 bg-green-200 rounded-full opacity-75"></div>
              </div>
            )}
          </div>

          {/* Success Message */}
          <div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">
              Registration Successful!
            </h2>
            <p id="success-description" className="text-gray-600">
              Student has been successfully registered with Navanidhi Academy
            </p>
          </div>

          {/* Student Details */}
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-4 space-y-3">
              <div className="flex items-center justify-center mb-3">
                <Building2 className="h-5 w-5 text-blue-600 mr-2" />
                <span className="font-semibold text-blue-900">Navanidhi Academy</span>
              </div>

