import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import DashboardLayout from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Plus, BookOpen, FileText, Edit2, ArrowRightLeft } from 'lucide-react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';

export default function ClassSubjectManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // States for adding class
  const [newClassName, setNewClassName] = useState('');
  const [newClassDescription, setNewClassDescription] = useState('');

  // States for adding subject
  const [newSubjectName, setNewSubjectName] = useState('');
  const [selectedClassesForSubject, setSelectedClassesForSubject] = useState<string[]>([]);
  const [selectedClassForView, setSelectedClassForView] = useState('');

  // States for editing subject
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingSubject, setEditingSubject] = useState<any>(null);
  const [editSubjectName, setEditSubjectName] = useState('');
  const [editSelectedClass, setEditSelectedClass] = useState('');

  // States for editing class
  const [isEditClassModalOpen, setIsEditClassModalOpen] = useState(false);
  const [editingClass, setEditingClass] = useState<any>(null);
  const [editClassName, setEditClassName] = useState('');
  const [editClassDescription, setEditClassDescription] = useState('');

  // Fetch classes and subjects
  const { data: classes = [], isLoading: classesLoading } = useQuery({
    queryKey: ['/api/classes'],
  });

  const { data: allSubjects = [], isLoading: subjectsLoading } = useQuery({
    queryKey: ['/api/admin/subjects'],
  });

  // Filter subjects based on selected class for viewing
  const filteredSubjects = selectedClassForView && selectedClassForView !== 'all'
    ? allSubjects.filter((subject: any) => subject.classId === selectedClassForView)
    : allSubjects;

  // Add class mutation
  const addClassMutation = useMutation({
    mutationFn: async (classData: { name: string; description: string }) => {
      return apiRequest('POST', '/api/admin/classes', classData);
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Class added successfully',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/classes'] });
      setNewClassName('');
      setNewClassDescription('');
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to add class',
        variant: 'destructive',
      });
    },
  });

  // Add subject mutation
  const addSubjectMutation = useMutation({
    mutationFn: async (subjectData: { name: string; classIds: string[] }) => {
      return apiRequest('POST', '/api/admin/subjects', subjectData);
    },
    onSuccess: () => {
      const classNames = selectedClassesForSubject.map(classId => getClassName(classId)).join(', ');
      toast({
        title: 'Success',
        description: `Subject "${newSubjectName}" successfully connected to: ${classNames}`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/subjects'] });
      setNewSubjectName('');
      setSelectedClassesForSubject([]);
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to add subject',
        variant: 'destructive',
      });
    },
  });

  // Edit subject mutation
  const editSubjectMutation = useMutation({
    mutationFn: async (subjectData: { id: string; name: string; classId: string }) => {
      return apiRequest('PUT', `/api/admin/subjects/${subjectData.id}`, {
        name: subjectData.name,
        classId: subjectData.classId
      });
    },
    onSuccess: () => {
      const className = getClassName(editSelectedClass);
      toast({
        title: 'Success',
        description: `Subject "${editSubjectName}" successfully reassigned to ${className}`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/subjects'] });
      setIsEditModalOpen(false);
      setEditingSubject(null);
      setEditSubjectName('');
      setEditSelectedClass('');
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to update subject',
        variant: 'destructive',
      });
    },
  });

  // Edit class mutation
  const editClassMutation = useMutation({
    mutationFn: async (classData: { id: string; name: string; description: string }) => {
      return apiRequest('PUT', `/api/admin/classes/${classData.id}`, {
        name: classData.name,
        description: classData.description
      });
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: `Class "${editClassName}" updated successfully`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/classes'] });
      setIsEditClassModalOpen(false);
      setEditingClass(null);
      setEditClassName('');
      setEditClassDescription('');
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to update class',
        variant: 'destructive',
      });
    },
  });

  // Delete class mutation
  const deleteClassMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest('DELETE', `/api/admin/classes/${id}`);
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Class deleted successfully',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/classes'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete class',
        variant: 'destructive',
      });
    },
  });

  // Delete subject mutation
  const deleteSubjectMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest('DELETE', `/api/admin/subjects/${id}`);
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Subject deleted successfully',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/subjects'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete subject',
        variant: 'destructive',
      });
    },
  });

  const handleAddClass = () => {
    if (!newClassName.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter a class name',
        variant: 'destructive',
      });
      return;
    }

    addClassMutation.mutate({
      name: newClassName.trim(),
      description: newClassDescription.trim(),
    });
  };

  const handleAddSubject = () => {
    if (!newSubjectName.trim() || selectedClassesForSubject.length === 0) {
      toast({
        title: 'Error',
        description: 'Please enter subject name and select at least one class',
        variant: 'destructive',
      });
      return;
    }

    addSubjectMutation.mutate({
      name: newSubjectName.trim(),
      classIds: selectedClassesForSubject,
    });
  };

  const handleEditSubject = (subject: any) => {
    setEditingSubject(subject);
    setEditSubjectName(subject.name);
    setEditSelectedClass(subject.classId);
    setIsEditModalOpen(true);
  };

  const handleUpdateSubject = () => {
    if (!editSubjectName.trim() || !editSelectedClass) {
      toast({
        title: 'Error',
        description: 'Please enter subject name and select a class',
        variant: 'destructive',
      });
      return;
    }

    editSubjectMutation.mutate({
      id: editingSubject.id,
      name: editSubjectName.trim(),
      classId: editSelectedClass,
    });
  };

  const handleEditClass = (cls: any) => {
    setEditingClass(cls);
    setEditClassName(cls.name);
    setEditClassDescription(cls.description || '');
    setIsEditClassModalOpen(true);
  };

  const handleUpdateClass = () => {
    if (!editClassName.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter class name',
        variant: 'destructive',
      });
      return;
    }

    editClassMutation.mutate({
      id: editingClass.id,
      name: editClassName.trim(),
      description: editClassDescription.trim(),
    });
  };

  const getClassName = (classId: string) => {
    return classes.find((c: any) => c.id === classId)?.name || 'Unknown';
  };

  return (
    <DashboardLayout
      title="Class & Subject Management"
      subtitle="Add new classes and subjects to the academic structure"
    >
      <Tabs defaultValue="classes" className="space-y-6">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="classes">Add Classes</TabsTrigger>
          <TabsTrigger value="subjects">Add Subjects</TabsTrigger>
        </TabsList>

        <TabsContent value="classes">
          <div className="grid gap-6 md:grid-cols-2">
            {/* Add Class Form */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <BookOpen size={20} />
                  <span>Add New Class</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="className">Class Name *</Label>
                  <Input
                    id="className"
                    placeholder="e.g., Class 10, Grade 5"
                    value={newClassName}
                    onChange={(e) => setNewClassName(e.target.value)}
                  />
                </div>
                <div>
                  <Label htmlFor="classDescription">Description</Label>
                  <Input
                    id="classDescription"
                    placeholder="Optional description"
                    value={newClassDescription}
                    onChange={(e) => setNewClassDescription(e.target.value)}
                  />
                </div>
                <Button
                  onClick={handleAddClass}
                  disabled={addClassMutation.isPending}
                  className="w-full"
                >
                  <Plus className="mr-2" size={16} />
                  {addClassMutation.isPending ? 'Adding...' : 'Add Class'}
                </Button>
              </CardContent>
            </Card>

            {/* Existing Classes List */}
            <Card>
              <CardHeader>
                <CardTitle>Existing Classes</CardTitle>
              </CardHeader>
              <CardContent>
                {classesLoading ? (
                  <div className="text-center py-4">Loading classes...</div>
                ) : classes.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    <BookOpen className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                    <p>No classes found. Add your first class!</p>
                  </div>
                ) : (
                  <div className="space-y-2 max-h-64 overflow-y-auto">
                    {classes.map((cls: any) => (
                      <div key={cls.id} className="p-3 border rounded-lg flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold">{cls.name}</h3>
                          {cls.description && (
                            <p className="text-sm text-gray-600">{cls.description}</p>
                          )}
                        </div>
                        <div className="flex items-center space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleEditClass(cls)}
                          >
                            <Edit2 className="w-4 h-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              if (confirm('Are you sure you want to delete this class?')) {
                                deleteClassMutation.mutate(cls.id);
                              }
                            }}
                            disabled={deleteClassMutation.isPending}
                          >
                            <ArrowRightLeft className="w-4 h-4 text-red-500" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="subjects">
          <div className="grid gap-6 md:grid-cols-2">
            {/* Add Subject Form */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <FileText size={20} />
                  <span>Add New Subject</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="subjectName">Subject Name *</Label>
                  <Input
                    id="subjectName"
                    placeholder="e.g., Mathematics, Science"
                    value={newSubjectName}
                    onChange={(e) => setNewSubjectName(e.target.value)}
                  />
                </div>
                <div>
                  <Label>Select Classes * (Choose multiple classes for this subject)</Label>
                  <div className="border rounded-md p-3 space-y-2 max-h-40 overflow-y-auto">
                    {classes.map((cls: any) => (
                      <div key={cls.id} className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id={`class-${cls.id}`}
                          checked={selectedClassesForSubject.includes(cls.id)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSelectedClassesForSubject([...selectedClassesForSubject, cls.id]);
                            } else {
                              setSelectedClassesForSubject(selectedClassesForSubject.filter(id => id !== cls.id));
                            }
                          }}
                          className="rounded"
                        />
                        <Label htmlFor={`class-${cls.id}`} className="text-sm">
                          {cls.name}
                        </Label>
                      </div>
                    ))}
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    Selected: {selectedClassesForSubject.length} class{selectedClassesForSubject.length !== 1 ? 'es' : ''}
                  </div>
                </div>
                <Button
                  onClick={handleAddSubject}
                  disabled={addSubjectMutation.isPending || classes.length === 0}
                  className="w-full"
                >
                  <Plus className="mr-2" size={16} />
                  {addSubjectMutation.isPending ? 'Adding...' : 'Add Subject'}
                </Button>
                {classes.length === 0 && (
                  <p className="text-sm text-amber-600 text-center">
                    Please add classes first before adding subjects
                  </p>
                )}
              </CardContent>
            </Card>

            {/* Existing Subjects List - Grouped by Class */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <span>Existing Subjects (Grouped by Class)</span>
                  <div className="text-sm text-gray-600">
                    Total: {allSubjects.length} subjects across {classes.length} classes
                  </div>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {subjectsLoading ? (
                  <div className="text-center py-4">Loading subjects...</div>
                ) : classes.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    <BookOpen className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                    <p>No classes found. Please add classes first!</p>
                  </div>
                ) : (
                  <div className="space-y-6">
                    {classes.map((cls: any) => {
                      const classSubjects = allSubjects.filter((subject: any) => subject.classId === cls.id);
                      return (
                        <div key={cls.id} className="border rounded-lg bg-gradient-to-r from-gray-50 to-blue-50 p-4">
                          <div className="flex items-center justify-between mb-4">
                            <div className="flex items-center gap-3">
                              <BookOpen className="w-5 h-5 text-blue-600" />
                              <h3 className="text-lg font-semibold text-gray-800">{cls.name}</h3>
                              <span className="text-sm bg-blue-100 text-blue-700 px-2 py-1 rounded-full">
                                {classSubjects.length} subjects
                              </span>
                            </div>
                            {cls.description && (
                              <p className="text-sm text-gray-600 italic">{cls.description}</p>
                            )}
                          </div>

                          {classSubjects.length === 0 ? (
                            <div className="text-center py-6 text-gray-500 border-2 border-dashed border-gray-200 rounded-lg">
                              <FileText className="mx-auto h-8 w-8 text-gray-400 mb-2" />
                              <p className="text-sm">No subjects added to this class yet</p>
                              <p className="text-xs text-gray-400 mt-1">Use the form above to add subjects</p>
                            </div>
                          ) : (
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                              {classSubjects.map((subject: any) => (
                                <div key={subject.id} 
                                     className="p-3 border rounded-lg bg-white hover:shadow-md transition-all duration-200 cursor-pointer hover:border-blue-300"
                                     onClick={() => handleEditSubject(subject)}>
                                  <div className="flex items-center justify-between">
                                    <div className="flex-1">
                                      <h4 className="font-medium text-gray-800 mb-1">{subject.name}</h4>
                                      <div className="flex items-center text-xs text-gray-500">
                                        <span className="bg-green-100 text-green-700 px-2 py-1 rounded-full">
                                          Active
                                        </span>
                                      </div>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      <Button
                                        variant="ghost"
                                        size="sm"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          handleEditSubject(subject);
                                        }}
                                        className="text-blue-600 hover:text-blue-800 hover:bg-blue-50"
                                      >
                                        <Edit2 className="w-4 h-4" />
                                      </Button>
                                      <Button
                                        variant="ghost"
                                        size="sm"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          if (confirm(`Are you sure you want to delete "${subject.name}" from ${cls.name}?`)) {
                                            deleteSubjectMutation.mutate(subject.id);
                                          }
                                        }}
                                        disabled={deleteSubjectMutation.isPending}
                                        className="text-red-500 hover:text-red-700 hover:bg-red-50"
                                      >
                                        <ArrowRightLeft className="w-4 h-4" />
                                      </Button>
                                    </div>
                                  </div>
                                  <div className="mt-2 text-xs text-blue-600 bg-blue-50 px-2 py-1 rounded flex items-center">
                                    <Edit2 className="w-3 h-3 mr-1" />
                                    Click to edit or reassign to another class
                                  </div>
                                </div>
                              ))}
                            </div>
                          )}
                        </div>
                      );
                    })}

                    {allSubjects.length === 0 && (
                      <div className="text-center py-12 text-gray-500">
                        <FileText className="mx-auto h-16 w-16 text-gray-400 mb-4" />
                        <h3 className="text-xl font-semibold mb-2">No Subjects Added Yet</h3>
                        <p className="text-gray-600 max-w-md mx-auto leading-relaxed">
                          Start building your academic structure by adding subjects to your classes using the form above.
                        </p>
                      </div>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>

      {/* Edit Subject Modal */}
      <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Subject: {editingSubject?.name}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-2">
            <div>
              <Label htmlFor="editSubjectName">Subject Name *</Label>
              <Input
                id="editSubjectName"
                value={editSubjectName}
                onChange={(e) => setEditSubjectName(e.target.value)}
              />
            </div>
            <div>
              <Label htmlFor="editClassSelect">Select Class *</Label>
              <Select value={editSelectedClass} onValueChange={setEditSelectedClass}>
                <SelectTrigger>
                  <SelectValue placeholder="Choose a class" />
                </SelectTrigger>
                <SelectContent>
                  {classes.map((cls: any) => (
                    <SelectItem key={cls.id} value={cls.id}>
                      {cls.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditModalOpen(false)}>Cancel</Button>
            <Button onClick={handleUpdateSubject} disabled={editSubjectMutation.isPending}>
              {editSubjectMutation.isPending ? 'Updating...' : 'Update Subject'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Class Modal */}
      <Dialog open={isEditClassModalOpen} onOpenChange={setIsEditClassModalOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Class: {editingClass?.name}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-2">
            <div>
              <Label htmlFor="editClassName">Class Name *</Label>
              <Input
                id="editClassName"
                value={editClassName}
                onChange={(e) => setEditClassName(e.target.value)}
                placeholder="e.g., Class 10, Grade 5"
              />
            </div>
            <div>
              <Label htmlFor="editClassDescription">Description</Label>
              <Input
                id="editClassDescription"
                value={editClassDescription}
                onChange={(e) => setEditClassDescription(e.target.value)}
                placeholder="Optional description"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsEditClassModalOpen(false)}>Cancel</Button>
            <Button onClick={handleUpdateClass} disabled={editClassMutation.isPending}>
              {editClassMutation.isPending ? 'Updating...' : 'Update Class'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </DashboardLayout>
  );
}

